Method.Source:
[Form]
public class DocPrintMgmtUtilsEmailsDiagResults extends FormRun
{
    private boolean dataSourceInitialized = false;
    private str callerMenuItem;

}

/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    super();

    callerMenuItem = element.args().menuItemName();

    element.setFormControls();
}

/// <summary>
/// Sets form controls properties.
/// </summary>
private void setFormControls()
{
    switch (callerMenuItem)
    {
        // Customers with invalid email address
        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithInvalidEmailsDiagResults):
            element.design().caption(strFmt('%1 - %2', "@DocentricAX4:InvalidEmailContacts", "@SYS11307"));
            Instructions.text("@DocentricAX4:InvalidCustomerEmailContactsInstructionTxt");
            DirPartyContactInfoAdvanced.visible(true);
            CheckContactInfo.helpText("@DocentricAX4:ShowInvalidAddressForCustomerBtnHelp");
            PartiesWithoutEmailAddressesResultsGrid.visible(false);
            break;

        // Customers without email address
        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithoutEmailsDiagResults):
            element.design().caption("@DocentricAX4:CustomersWithoutEmailAddressFormCaption");
            Instructions.text("@DocentricAX4:CustomersWithoutEmailContactInstructionTxt");
            element.design().dialogSize(DialogSize::Medium);
            PartiesWithInvalidEmailAddressesResultsGrid.visible(false);
            CheckContactInfo.helpText("@DocentricAX4:ShowCustomersWithoutEmailAddressBtnHelp");
            PartiesWithoutEmailAddressesResultsGrid.dataSource(tableStr(CustTable));
            AccountNumWithoutEmail.dataSource(tableStr(CustTable));
            AccountNumWithoutEmail.dataField(fieldNum(CustTable, AccountNum));
            AccountNameWithoutEmail.dataSource(tableStr(CustTable));
            AccountNameWithoutEmail.dataMethod(tableMethodStr(CustTable, name));
            break;

        // Vendors with invalid email address
        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithInvalidEmailsDiagResults):
            element.design().caption(strFmt('%1 - %2', "@DocentricAX4:InvalidEmailContacts", "@SYS9449"));
            Instructions.text("@DocentricAX4:InvalidVendorEmailContactsInstructionTxt");
            DirPartyContactInfoAdvanced.visible(true);
            CheckContactInfo.helpText("@DocentricAX4:ShowInvalidAddressForVendorBtnHelp");
            PartiesWithoutEmailAddressesResultsGrid.visible(false);
            AccountNum.label("@SYS14048");
            break;

        // Vendors without email address
        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithoutEmailsDiagResults):
            element.design().caption("@DocentricAX4:VendorsWithoutEmailAddressFormCaption");
            Instructions.text("@DocentricAX4:VendorsWithoutEmailContactInstructionTxt");
            element.design().dialogSize(DialogSize::Medium);
            PartiesWithInvalidEmailAddressesResultsGrid.visible(false);
            CheckContactInfo.helpText("@DocentricAX4:ShowVendorsWithoutEmailAddressBtnHelp");
            PartiesWithoutEmailAddressesResultsGrid.dataSource(tableStr(VendTable));
            AccountNumWithoutEmail.dataSource(tableStr(VendTable));
            AccountNumWithoutEmail.dataField(fieldNum(VendTable, AccountNum));
            AccountNameWithoutEmail.dataSource(tableStr(VendTable));
            AccountNameWithoutEmail.dataMethod(tableMethodStr(VendTable, name));
            break;

        default:
            DocGlobalHelper::handleException(strFmt('The form is open via an unexpected menu item (%1)', callerMenuItem));
    }
}

/// <summary>
/// Builds a form data source to list customers without email contacts.
/// </summary>
private void customersWithoutEmails()
{
    if (!dataSourceInitialized)
    {
        CustTable_ds.queryBuildDataSource().clearRanges();

        QueryBuildDataSource qbdsWE = CustTable_ds.queryBuildDataSource().addDataSource(tableNum(CustTable), 'CustTableWithEmail');
        qbdsWE.addLink(fieldNum(CustTable, RecId), fieldNum(CustTable, RecId));
        qbdsWE.joinMode(JoinMode::NoExistsJoin);

        QueryBuildDataSource qbdsDPL = qbdsWE.addDataSource(tableNum(DirPartyLocation), 'DirPartyLocation');
        qbdsDPL.addLink(fieldNum(CustTable, Party), fieldNum(DirPartyLocation, Party), 'CustTableWithEmail');
        qbdsDPL.joinMode(JoinMode::ExistsJoin);

        QueryBuildDataSource qbdsLEA = qbdsDPL.addDataSource(tableNum(LogisticsElectronicAddress), 'LogisticsElectronicAddress');
        qbdsLEA.addLink(fieldNum(DirPartyLocation, Location), fieldNum(LogisticsElectronicAddress, Location));
        qbdsLEA.addRange(fieldNum(LogisticsElectronicAddress, Type)).value(queryValue(LogisticsElectronicAddressMethodType::Email));
        qbdsLEA.joinMode(JoinMode::ExistsJoin);

        dataSourceInitialized = true;
    }

    CustTable_ds.executeQuery();

    if (!CustTable.RecId)
    {
        DocGlobalHelper::handleInfo("@DocentricAX4:NoCustomersWithoutEmailAddressMsg");
    }
}

/// <summary>
/// Builds a form data source to list vendors without email contacts.
/// </summary>
private void vendorsWithoutEmails()
{
    if (!dataSourceInitialized)
    {
        VendTable_ds.queryBuildDataSource().clearRanges();

        QueryBuildDataSource qbdsWE = VendTable_ds.queryBuildDataSource().addDataSource(tableNum(VendTable), 'VendTableWithEmail');
        qbdsWE.addLink(fieldNum(VendTable, RecId), fieldNum(VendTable, RecId));
        qbdsWE.joinMode(JoinMode::NoExistsJoin);

        QueryBuildDataSource qbdsDPL = qbdsWE.addDataSource(tableNum(DirPartyLocation), 'DirPartyLocation');
        qbdsDPL.addLink(fieldNum(VendTable, Party), fieldNum(DirPartyLocation, Party), 'VendTableWithEmail');
        qbdsDPL.joinMode(JoinMode::ExistsJoin);

        QueryBuildDataSource qbdsLEA = qbdsDPL.addDataSource(tableNum(LogisticsElectronicAddress), 'LogisticsElectronicAddress');
        qbdsLEA.addLink(fieldNum(DirPartyLocation, Location), fieldNum(LogisticsElectronicAddress, Location));
        qbdsLEA.addRange(fieldNum(LogisticsElectronicAddress, Type)).value(queryValue(LogisticsElectronicAddressMethodType::Email));
        qbdsLEA.joinMode(JoinMode::ExistsJoin);

        dataSourceInitialized = true;
    }

    VendTable_ds.executeQuery();

    if (!VendTable.RecId)
    {
        DocGlobalHelper::handleInfo("@DocentricAX4:NoVendorsWithoutEmailAddressMsg");
    }
}

/// <summary>
/// Builds data source for invalid Email contacts for both, customers and vendors.
/// </summary>
private void invalidEmailContacts()
{
    if (!dataSourceInitialized)
    {
        DocPartiesEmailContactView_ds.queryBuildDataSource().clearRange(fieldNum(DocPartiesEmailContactView, RecId));
        DocPrintMgmtUtilsEmailsDiagTmp_ds.queryBuildDataSource().addLink(fieldNum(DocPartiesEmailContactView, RecId), fieldNum(DocPrintMgmtUtilsEmailsDiagTmp, RefRecId));
        dataSourceInitialized = true;
    }

    DocPartiesEmailContactView_ds.executeQuery();

    if (!DocPrintMgmtUtilsEmailsDiagTmp.RefRecId)
    {
        DocGlobalHelper::handleInfo("@DocentricAX4:NoInvalidEmailAddressesMsg");
    }
}

/// <summary>
/// Initializes the datasource.
/// </summary>
public void init()
{
    super();

    this.queryBuildDataSource().addLink(fieldNum(DocPartiesEmailContactView, RecId), fieldNum(LogisticsElectronicAddress, RecId));
}

/// <summary>
/// Initializes the datasource.
/// </summary>
public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(CustTable, RecId)).value(queryValue(0));
}

/// <summary>
/// Initializes the datasource.
/// </summary>
public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(VendTable, RecId)).value(queryValue(0));
}

/// <summary>
/// Initializes the datasource.
/// </summary>
public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(DocPartiesEmailContactView, PartyDataAreaId)).value(queryValue(curExt()));
    this.queryBuildDataSource().addRange(fieldNum(DocPartiesEmailContactView, RecId)).value(queryValue(0));
    this.queryBuildDataSource().addSortField(fieldNum(DocPartiesEmailContactView, AccountNum));

    if (element.args().menuItemName() == menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithInvalidEmailsDiagResults))
    {
        this.queryBuildDataSource().addRange(fieldNum(DocPartiesEmailContactView, AccountRole)).value(queryValue(DocAccountRole::Customer));
    }
    else if (element.args().menuItemName() == menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithInvalidEmailsDiagResults))
    {
        this.queryBuildDataSource().addRange(fieldNum(DocPartiesEmailContactView, AccountRole)).value(queryValue(DocAccountRole::Vendor));
    }
}

/// <summary>
/// Jumps to the referenced form.
/// </summary>
public void jumpRef()
{
    // When AccountNum field is clicked, open the customer or vendor form
    // and get positioned to the exactly the same record as this one.

    Args args = new Args();
    args.caller(element);

    if (DocPartiesEmailContactView.AccountRole == DocAccountRole::Customer)
    {
        args.record(CustTable::find(DocPartiesEmailContactView.AccountNum));
        args.formViewOption(FormViewOption::Details);

        new MenuFunction(menuItemDisplayStr(CustTableListPage), MenuItemType::Display).run(args);
    }
    else if (DocPartiesEmailContactView.AccountRole == DocAccountRole::Vendor)
    {
        args.record(VendTable::find(DocPartiesEmailContactView.AccountNum));
        args.formViewOption(FormViewOption::Details);

        new MenuFunction(menuItemDisplayStr(VendTableListPage), MenuItemType::Display).run(args);
    }
}

/// <summary>
/// Depending on the caller menu item, run check for customers/vendors without Email contact
/// or check for invalid customer/vendor Email contacts.
/// </summary>
public void clicked()
{
    switch (callerMenuItem)
    {
        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithoutEmailsDiagResults):
            element.customersWithoutEmails();
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithoutEmailsDiagResults):
            element.vendorsWithoutEmails();
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsCustomersWithInvalidEmailsDiagResults):
            DocPrintMgmtUtilsEmailsDiagTmp::getCustomerInvalidEmails(DocPrintMgmtUtilsEmailsDiagTmp);
            element.invalidEmailContacts();
            break;

        case menuItemDisplayStr(DocPrintMgmtUtilsVendorsWithInvalidEmailsDiagResults):
            DocPrintMgmtUtilsEmailsDiagTmp::getVendorInvalidEmails(DocPrintMgmtUtilsEmailsDiagTmp);
            element.invalidEmailContacts();
            break;

        default:
            DocGlobalHelper::handleException(strFmt('Invalid caller menu item (%1)', callerMenuItem));
    }
}
