public class PIPApprovedCustomerGroup extends FormRun implements InventItemIdProvider
{
    DateEffectivenessFormController dateEffectivenessFormController;
    PIPApprovedCustomerGroupId      approvedCustomerGroup;

    InventDimCtrl_Frm               inventDimFormSetup;

    private static InventDimGroupSetup emptySetup = InventDimGroupSetup::construct();

}

public InventDimCtrl_Frm inventDimSetupObject()
{
    return inventDimFormSetup;
}

public ItemId ItemId()
{
    return PIPApprovedCustomerList.ItemCode == TableGroupAll::Table ? PIPApprovedCustomerList.ItemRelation : '';
}

public void init()
{
    super();

    dateEffectivenessFormController = DateEffectivenessFormController::newFormControls(PIPApprovedCustomerList_DS,
                                                                            DateEffectivenessText,
                                                                            ButtonViewAll,
                                                                            ButtonViewCurrent,
                                                                            RemoveItemRelation,
