    dateEffectivenessFormController.setViewState(DateEffectivenessViewState::All);

    this.updateDesign();
    this.updateDesignItemsGrid(InventDimFormDesignUpdate::Init);
}

public void refreshCustomerList()
{
    if (pipApprovedCustomerGroup.RecId)
    {
        delete_from TmpPIPCustomerList;

        TmpPIPCustomerList.setTmpData(TmpPIPCustomerList::loadCustomers(pipApprovedCustomerGroup.ApprovedCustomerGroup));            
    }

    TmpPIPCustomerList_DS.research();
}

/// <summary>
/// Creates the UI which alllows the user to enter the query based on which customers are added to an intermediate list.
/// </summary>
void selectCustomers()
{
    Query                   customerQuery = new Query(queryStr(PIPCustTable));
    QueryRun                customerQueryRun;
    QueryBuildDataSource    qbdsCustTable;
    QueryBuildRange         qbrApprovedCustomerGroup;
    CustTable               custTableQuery;
    CustTable               custTableForm;

    //BP Deviation documented
    custTableForm.setTmp();
    custTableForm.setTmpData(custTable);

    qbdsCustTable               = customerQuery.dataSourceTable(tableNum(CustTable));
    qbrApprovedCustomerGroup    = qbdsCustTable.addRange(fieldNum(CustTable, PIPApprovedCustomerGroup));
    qbrApprovedCustomerGroup.value(SysQuery::valueNot(pipApprovedCustomerGroup.ApprovedCustomerGroup));
    qbrApprovedCustomerGroup.status(1);

    customerQueryRun = new QueryRun(customerQuery);
    if (customerQueryRun.prompt())
    {
        ttsbegin;
        while (customerQueryRun.next())
        {
            custTableQuery = customerQueryRun.get(tableNum(CustTable)) as CustTable;

            select firstonly custTableForm
                where custTableForm.AccountNum == custTableQuery.AccountNum;

            if (!custTableForm)
            {
                custTableForm.data(custTableQuery.data());
                custTableForm.doInsert();
            }
        }
        ttscommit;
        custTable_ds.research();
    }

    this.updateDesign();
}

/// <summary>
/// Performs the actual addition of items to the Approved customer group from an intermediate list.
/// </summary>
public void addCustomersToList()
{
    int             customersAlreadyAssigned;
    CustTable       customersToUpdate;
    CustTable       custTableTmp;

    //BP Deviation documented
    custTableTmp.setTmp();
    custTableTmp.setTmpData(custTable);

    select count(RecId) from custTableTmp where custTableTmp.PIPApprovedCustomerGroup;

    customersAlreadyAssigned = any2int(custTableTmp.RecId);

    if (!customersAlreadyAssigned
        || (customersAlreadyAssigned
            && Box::okCancel(strFmt("@PIP4036", customersAlreadyAssigned, pipApprovedCustomerGroup.ApprovedCustomerGroup), DialogButton::Ok) == DialogButton::Ok))
    {
        while select forUpdate custTable
        {
            customersToUpdate = CustTable::find(custTable.AccountNum, true);
            customersToUpdate.PIPApprovedCustomerGroup = pipApprovedCustomerGroup.ApprovedCustomerGroup;
            customersToUpdate.update();
        }

        delete_from custTable;

        element.refreshCustomerList();
    }
}

public void deleteSelectedCustomers()
{
    MultiSelectionHelper    selectionHelper = MultiSelectionHelper::construct();
    Set                     selectedRecords = new Set(Types::Record);
    CustTable               customer;

    selectionHelper.parmDatasource(custTable_DS);

    customer = selectionHelper.getFirst();

    while (customer)
    {
        custTable_ds.positionToRecord(customer);
        if (custTable.isTmp())
        {
            custTable.delete();
        }

        customer = selectionHelper.getNext();
    }

    custTable_DS.research();

    this.updateDesign();
}

public void updateDesignItemsGrid(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_ItemBarcode::newFromForm(element);
            }
            inventDimFormSetup.parmSkipOnHandLookUp(true);
        // do continue
        case InventDimFormDesignUpdate::Active:
            if (PIPApprovedCustomerList.ItemCode == TableGroupAll::Table)
            {
                inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(PIPApprovedCustomerList.ItemRelation));
            }
            else
            {
                inventDimFormSetup.formActiveSetup(emptySetup);
            }
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            if (PIPApprovedCustomerList.ItemCode == TableGroupAll::Table)
            {
                inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(PIPApprovedCustomerList.ItemRelation));
            }
            else
            {
                inventDimFormSetup.formActiveSetup(emptySetup);
            }
            inventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void updateDesign()
{
    RemoveButton.enabled(custTable.RecId);
    AddToList.enabled(custTable.RecId);
}

public int active()
{
    int ret;

    ret = super();

    if (ret && custTable && approvedCustomerGroup != pipApprovedCustomerGroup.ApprovedCustomerGroup)
    {
        if (Box::okCancel(strFmt("@PIP4038", approvedCustomerGroup), DialogButton::Ok) == DialogButton::Ok)
        {
            delete_from custTable;
        }
        else
        {
            PIPApprovedCustomerGroup_DS.positionToRecord(PIPApprovedCustomerGroup::find(approvedCustomerGroup));
        }
    }

    approvedCustomerGroup = pipApprovedCustomerGroup.ApprovedCustomerGroup;

    element.refreshCustomerList();
    CustomerList.setFocus();
    element.updateDesign();

    return ret;
}

public void executeQuery()
{
    QueryBuildDataSource qbdsACL = this.queryBuildDataSource();

    qbdsACL.clearRanges();
    qbdsACL.addRange(fieldNum(PIPApprovedCustomerList, CustomerCode)).value(queryValue(TableGroupAll::GroupId));
    qbdsACL.addRange(fieldNum(PIPApprovedCustomerList, CustomerRelation)).value(pipApprovedCustomerGroup.ApprovedCustomerGroup);
    super();
}

