public class PIPApprovedCustomerList extends FormRun implements InventItemIdProvider
{
    PIPFormCtrl_ApprovedCustList    formControl;
    DateEffectivenessFormController dateEffectivenessFormController;

    PIPApprovedCustomerGroup        customerGroup;
    PIPApprovedItemGroup            itemGroup;

    InventDimCtrl_Frm               inventDimFormSetup;

    private static InventDimGroupSetup emptySetup = InventDimGroupSetup::construct();

}

public void createSetFocusOnGrid()
{
    if (formControl.parmShowCustomerGrid())
    {
        PIPApprovedCustomerList_CustomerRelation.setFocus();
    }
    else if (formControl.parmShowItemGrid())
    {
        PIPApprovedCustomerList_ItemRelation.setFocus();
    }
}

public void formRefreshQuery()
{
    PIPApprovedCustomerList_DS.executeQuery();
    inventTable_ds.executeQuery();
    inventTable_ds.cacheCalculateMethod(tableMethodStr(InventTable, itemName));
}

public edit validFromDate fromDate(boolean _set, validFromDate _fromDate)
{
    return formControl.editHandlerFromDate(_set, _fromDate);
}

public edit PIPParmDateNullInclude includeNoExpiration(boolean _set, NoYesId _includeNoExpiration)
{
    return formControl.editHandlerIncludeNoExpiration(_set, _includeNoExpiration);
}

public InventDimCtrl_Frm inventDimSetupObject()
{
    return inventDimFormSetup;
}

public ItemId ItemId()
{
    return PIPApprovedCustomerList.ItemCode == TableGroupAll::Table ? PIPApprovedCustomerList.ItemRelation : '';
}

public void init()
{
    Args args = element.args();

    if (!args
        || (args.dataset()
            && args.dataset() != tablenum(InventTable)
            && args.dataset() != tablenum(CustTable)
            && args.dataset() != tablenum(SalesTable)
            && args.dataset() != tableNum(PIPApprovedCustomerGroup)
            && args.dataset() != tableNum(PIPApprovedItemGroup))
            && Args.dataset() != tableNum(InventDimCombination))
    {
        throw error("@PRO467");
    }

    if (args.record() && args.dataset() == tableNum(PIPApprovedItemGroup))
    {
        itemGroup = args.record();
    }
    if (args.record() && args.dataset() == tableNum(PIPApprovedCustomerGroup))
    {
        customerGroup = args.record();
    }

    formControl = PIPFormCtrl_ApprovedCustList::newFromFormRun(this);
    formControl.initPre();

    super();

    dateEffectivenessFormController = DateEffectivenessFormController::newFormControls(PIPApprovedCustomerList_DS,
                                                                            DateEffectivenessText,
                                                                            ButtonViewAll,
                                                                            ButtonViewCurrent,
                                                                            RemoveRecord,
                                                                            EditRecord);

    dateEffectivenessFormController.setViewState(DateEffectivenessViewState::All);

    formControl.initPost();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

