public void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_ItemBarcode::newFromForm(element);
            }
            inventDimFormSetup.parmSkipOnHandLookUp(true);
        // do continue
        case InventDimFormDesignUpdate::Active:
            if (PIPApprovedCustomerList.ItemCode == TableGroupAll::Table)
            {
                inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(PIPApprovedCustomerList.ItemRelation));
            }
            else
            {
                inventDimFormSetup.formActiveSetup(emptySetup);
            }
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            if (PIPApprovedCustomerList.ItemCode == TableGroupAll::Table)
            {
                inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(PIPApprovedCustomerList.ItemRelation));
            }
            else
            {
                inventDimFormSetup.formActiveSetup(emptySetup);
            }
            inventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }

    PIPApprovedCustomerList_DS.object(fieldNum(PIPApprovedCustomerList, CustomerRelation)).enabled(PIPApprovedCustomerList.CustomerCode != TableGroupAll::All);
    PIPApprovedCustomerList_DS.object(fieldNum(PIPApprovedCustomerList, CustomerRelation)).skip(PIPApprovedCustomerList.CustomerCode == TableGroupAll::All);

    PIPApprovedCustomerList_DS.object(fieldNum(PIPApprovedCustomerList, ItemRelation)).enabled(PIPApprovedCustomerList.ItemCode != TableGroupAll::All);
    PIPApprovedCustomerList_DS.object(fieldNum(PIPApprovedCustomerList, ItemRelation)).skip(PIPApprovedCustomerList.ItemCode == TableGroupAll::All);
}

