public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    // Let the date effectiveness controller enable/disable editing of controls only if they can be edited in the first place.
    if (formControl.parmAllowEdit())
    {
        dateEffectivenessFormController.active();

        // Deletion is always allowed for AVL records, override this behavior from the date effectiveness controller.
        RemoveRecord.enabled(true);
    }

    return ret;
}

