[Form]
public class PIPApprovedItemGroup extends FormRun
{
    DateEffectivenessFormController dateEffectivenessFormController;
    PIPApprovedItemGroupId          approvedItemGroupId;

}

public void init()
{
    super();

    dateEffectivenessFormController = DateEffectivenessFormController::newFormControls(PIPApprovedCustomerList_DS,
                                                                            DateEffectivenessText,
                                                                            ButtonViewAll,
                                                                            ButtonViewCurrent,
                                                                            RemoveCustomerRelation,
                                                                            EditRecord);

    dateEffectivenessFormController.setViewState(DateEffectivenessViewState::All);

    this.updateDesign();
}

public void refreshItemList()
{
    if (pipApprovedItemGroup.RecId)
    {
        delete_from TmpPIPItemList;

        TmpPIPItemList.setTmpData(TmpPIPItemList::loadItems(pipApprovedItemGroup.ApprovedItemGroup));
    }

    TmpPIPItemList_DS.research();
}

/// <summary>
/// Creates the UI which alllows the user to enter the query based on which items are added to an intermediate list.
/// </summary>
void selectItems()
{
    Query                   itemQuery   = new Query(queryStr(EcoResProductPerCompanyListPage));
    QueryRun                itemQueryRun;
    QueryBuildDataSource    qbdsInventTable;
    QueryBuildRange         qbrApprovedItemGroup;
    InventTable             inventTableQuery;
    InventTable             inventTableForm;

    //BP Deviation documented
    inventTableForm.setTmp();
    inventTableForm.setTmpData(inventTable);

    qbdsInventTable         = itemQuery.dataSourceTable(tableNum(InventTable));
    qbrApprovedItemGroup    = qbdsInventTable.addRange(fieldNum(InventTable, PIPApprovedItemGroup));
    qbrApprovedItemGroup.value(SysQuery::valueNot(pipApprovedItemGroup.ApprovedItemGroup));
    qbrApprovedItemGroup.status(1);

    itemQueryRun = new QueryRun(itemQuery);
    if (itemQueryRun.prompt())
    {
        ttsbegin;
        while (itemQueryRun.next())
        {
            inventTableQuery = itemQueryRun.get(tableNum(InventTable)) as InventTable;

            select firstonly inventTableForm
                where inventTableForm.ItemId    == inventTableQuery.ItemId;

            if (!inventTableForm)
            {
                inventTableForm.data(inventTableQuery.data());
                inventTableForm.doInsert();
            }
        }
        ttscommit;
        inventTable_ds.research();
    }

    this.updateDesign();
}

/// <summary>
/// Performs the actual addition of items to the Approved item group from an intermediate list.
/// </summary>
public void addItemsToList()
{
    int             itemsAlreadyAssigned;
    InventTable     itemToUpdate;
    InventTable     inventTableTmp;

    //BP Deviation documented
    inventTableTmp.setTmp();
    inventTableTmp.setTmpData(inventTable);

    select count(RecId) from inventTableTmp where inventTableTmp.PIPApprovedItemGroup;

    itemsAlreadyAssigned = any2int(inventTableTmp.RecId);

    if (!itemsAlreadyAssigned
        || (itemsAlreadyAssigned
            && Box::okCancel(strFmt("@PIP4035", itemsAlreadyAssigned, pipApprovedItemGroup.ApprovedItemGroup), DialogButton::Ok) == DialogButton::Ok))
    {
        while select forUpdate inventTable
        {
            itemToUpdate = InventTable::find(inventTable.ItemId, true);
            itemToUpdate.PIPApprovedItemGroup = pipApprovedItemGroup.ApprovedItemGroup;
            itemToUpdate.update();
        }

        delete_from inventTable;

        element.refreshItemList();
    }
}

public void deleteSelectedItems()
{
    MultiSelectionHelper    selectionHelper = MultiSelectionHelper::construct();
    Set                     selectedRecords = new Set(Types::Record);
    InventTable             item;

    selectionHelper.parmDatasource(inventTable_DS);

    item = selectionHelper.getFirst();

    while (item)
    {
        inventTable_DS.positionToRecord(item);
        if (inventTable.isTmp())
        {
            inventTable.delete();
        }

        item = selectionHelper.getNext();
    }

    inventTable_DS.research();

    this.updateDesign();
}

public void updateDesign()
{
    RemoveButton.enabled(inventTable.RecId);
    AddToList.enabled(inventTable.RecId);
}

public int active()
{
    int ret;

    ret = super();

    if (ret && inventTable && approvedItemGroupId != pipApprovedItemGroup.ApprovedItemGroup)
    {
        if (Box::okCancel(strFmt("@PIP4037", approvedItemGroupId), DialogButton::Ok) == DialogButton::Ok)
        {
            delete_from inventTable;
        }
        else
        {
            PIPApprovedItemGroup_DS.positionToRecord(PIPApprovedItemGroup::find(approvedItemGroupId));
        }
    }

    approvedItemGroupId = pipApprovedItemGroup.ApprovedItemGroup;

    element.refreshItemList();
    ItemList.setFocus();
    element.updateDesign();

    return ret;
}

public void executeQuery()
{
    QueryBuildDataSource qbdsACL = this.queryBuildDataSource();

    qbdsACL.clearRanges();
    qbdsACL.addRange(fieldNum(PIPApprovedCustomerList, ItemCode)).value(queryValue(TableGroupAll::GroupId));
    qbdsACL.addRange(fieldNum(PIPApprovedCustomerList, ItemRelation)).value(pipApprovedItemGroup.ApprovedItemGroup);
    super();
}

public int active()
{
    int ret;

    ret = super();

    // Let the date effectiveness controller enable/disable editing of controls.
    dateEffectivenessFormController.active();

    return ret;
}

