    super();
    xSysLastValue::saveLast(this);
}

edit ValidFromDate fromDate(boolean _set, validFromDate _fromDate)
{
    if (_set)
    {
        dataProvider.parmStartDate(_fromDate);           
        element.optionsChanged();
    }

    return dataProvider.parmStartDate();
}

public void init()
{
    super();

    xSysLastValue::getLast(this);

    dataProvider = PIPCAPAAnalyticsDataProvider::construct();

    element.optionsChanged();
    element.updateChartVisibility();

    dataProvider.parmCAPACategory(capaCategory); 
}

