public void optionsChanged()
{
    mustUpdateWorker                = true;
    mustUpdateCategory              = true;
    mustUpdateTrend                 = true;
    mustUpdateType                  = true;
    mustUpdateRootCause             = true;

    mustUpdateSubCategory           = true;
    mustUpdateTrendingSubCategory   = true;
}

public container pack()
{
    return [#CurrentVersion, #CurrentList];
}

void updateChartVisibility()
{
    showGraphWorker     = openedByCheckBox.checked();
    showGraphCategory   = byCategoryCheckBox.checked();
    showGraphTrend      = trendByCategoryCheckBox.checked();
    showGraphType       = TypeCheckBox.checked();
    showGraphRootCause  = RootCauseCheckBox.checked();

    capaCategory                    = capaCategoryControl.valueStr();
    showGraphSubCategory            = bySubCategoryCheckBox.checked() && capaCategory;
    showGraphTrendingSubCategory    = trendingBySubCategoryCheckBox.checked() && capaCategory;

    casesByEmployee.visible(showGraphWorker);
    casesByCategory.visible(showGraphCategory);
    trendByCategory.visible(showGraphTrend);
    CasesByType.visible(showGraphType);
    CasesByRootCause.visible(showGraphRootCause);

    casesBySubCatgeory.visible(showGraphSubCategory);
    trendingBySubCategory.visible(showGraphTrendingSubCategory);
}

/// <summary>
///     Refreshes controls on the Overview tab based on user's selections
/// </summary>
void refreshControls()
{
    this.updateChartVisibility();

    if (casesByEmployee.visible() && mustUpdateWorker)
    {
        this.updateGraphWorker();
    }

    if (casesByCategory.visible() && mustUpdateCategory)
    {
        this.updateGraphCategory();
    }

    if (trendByCategory.visible() && mustUpdateTrend)
    {
        this.updateGraphTrendByCategory();
    }

    if (CasesByType.visible() && mustUpdateType)
    {
        this.updateGraphType();
    }

    if (CasesByRootCause.visible() && mustUpdateRootCause)
    {
        this.updateGraphRootCause();
    }

    if (casesBySubCatgeory.visible() && mustUpdateSubCategory)
    {
        this.updateGraphSubCategory();
    }

    if (trendingBySubCategory.visible() && mustUpdateTrendingSubCategory)
    {
        this.updateGraphTrendBySubCategory();
    }
}

edit ValidToDate toDate(boolean _set, validToDate _toDate)
{
    if (_set)
    {
        dataProvider.parmEndDate(_toDate);
        element.optionsChanged();
    }

    return dataProvider.parmEndDate();
}

public boolean unpack(container packedClass)
{
    Integer version = conPeek(packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = packedClass;
            break;
        default:
            return false;
    }

    openedByCheckBox.value(showGraphWorker);
    byCategoryCheckBox.value(showGraphCategory);
    trendByCategoryCheckBox.value(showGraphTrend);
    TypeCheckBox.value(showGraphType);
    RootCauseCheckBox.value(showGraphRootCause);
    capaCategoryControl.text(capaCategory);
    bySubCategoryCheckBox.value(showGraphSubCategory);
    trendingBySubCategoryCheckBox.value(showGraphTrendingSubCategory);

    return true;
}

/// <summary>
///     Generates or updates the 'Number of cases by category' graph
/// </summary>
void updateGraphCategory()
{
    List            graphData;
    ListEnumerator  graphDataEnumerator;
    container       data;

    #define.PosCategory(1)
    #define.PosCount(2)

    graphicsCategory = Graphics::newGraphicsNoTitlesSysChart(SysChartCasesByCategory, GraphicsTmpDataCategory);

    graphData = List::create(dataProvider.getCategoryGraphData());
    graphDataEnumerator = graphData.getEnumerator();
    while (graphDataEnumerator.moveNext())
    {
        data = graphDataEnumerator.current();
        graphicsCategory.loadData(conPeek(data, #PosCategory), '', conPeek(data, #PosCount));
    }

    this.showGraph(graphicsCategory);

    mustUpdateCategory = false;
}

void showGraph(Graphics _graphics)
{
    _graphics.parmCreateType(#CT_LEGEND);
    _graphics.create();
    _graphics.showGraph();
}

