/// <summary>
///     Generates or updates the 'Trending of cases by Category' graph
/// </summary>
void updateGraphTrendByCategory()
{
    str             strDate;
    List            graphData;
    ListEnumerator  graphDataEnumerator;
    container       data;

    #define.PosDate(1)
    #define.PosCategory(2)
    #define.PosCount(3)

    graphicsTrend = Graphics_Period::newGraphicsNoTitlesSysChart(SysChartCasesBySubCat, GraphicsTmpDataCategoryTrend);
    graphicsTrend.parmTitleXAxis("@SYS106478");

    if (dataProvider.parmEndDate() - dataProvider.parmStartDate() > #DaysInMonth)
    {
        graphicsTrend.parmPeriodX(GraphicsDateDim::YearMonth);
    }
    else
    {
        graphicsTrend.parmPeriodX(GraphicsDateDim::Month);
        graphicsTrend.parmTitleXAxis(strfmt("@SYS50120", year(dataProvider.parmEndDate())));
    }

    graphicsTrend.parmFromDate(dataProvider.parmStartDate());
    graphicsTrend.parmtoDate(dataProvider.parmEndDate());

    graphData = List::create(dataProvider.getTrendGraphData());
    graphDataEnumerator = graphData.getEnumerator();

    while (graphDataEnumerator.moveNext())
    {
        data    = graphDataEnumerator.current();
        strDate = date2StrUsr(conPeek(data, #PosDate), DateFlags::None);
        graphicsTrend.loadData(strDate, conPeek(data, #PosCategory), conPeek(data, #PosCount));
    }

    this.showGraph(graphicsTrend);

    mustUpdateTrend = false;
}

