public boolean allowPageDeactivate()
{
    boolean ret;

    ret = super();

    canSwitchToTab2 = openedByCheckBox.checked() || byCategoryCheckBox.checked() || trendByCategoryCheckBox.checked()
        || TypeCheckBox.checked() || RootCauseCheckBox.checked();
    canSwitchToTab3 = capaCategory && (bySubCategoryCheckBox.checked() || trendingBySubCategoryCheckBox.checked());

    element.refreshControls();

    return ret;
}

