public void lookup()
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;

    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(PIPCAPACategories), this);

    sysTableLookup.addLookupField(fieldNum(PIPCAPACategories, Category));
    sysTableLookup.addLookupField(fieldNum(PIPCAPACategories, Description));
    sysTableLookup.addLookupfield(fieldNum(PIPCAPACategories, SubCategoryEntityType));

    queryBuildDataSource = query.addDataSource(tableNum(PIPCAPACategories));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    PIPCAPACategories   pipCAPACategory;
    boolean             ret;

    ret = super();

    if (this.valueStr())
    {
        pipCAPACategory = PIPCAPACategories::find(this.valueStr());

        if (!pipCAPACategory)
        {
            ret = ret && checkFailed("@PIP1073");
        }
    }
    capaCategory = pipCAPACategory.Category;
    dataProvider.parmCAPACategory(capaCategory);
    element.optionsChanged();
    element.updateChartVisibility();

    return ret;
}

