public void pageActivated()
{
    super();

    if (!canSwitchToTab2)
    {
        Box::warning("@PIP1074");
        tabPageData.activatePage();
    }
    else
    {
        element.refreshControls();
    }
}

public void pageActivated()
{
    super();

    if (!canSwitchToTab3)
    {
        if (capaCategory)
        {
            Box::warning("@PIP1074");
        }
        else
        {
            Box::warning("@PIP1075");
        }
        tabPageData.activatePage();
    }
    else
    {
        element.refreshControls();
    }
}
