/// <summary>
///     Check whether a case document can be submitted. This method is called when
///     a workflow action is performed and work item ownership might be changed,
///     we need to set editable property of the case appropriately.
/// </summary>
/// <returns>
///     True if can be submitted.
/// </returns>
public boolean canSubmitToWorkflow()
{
    this.setCaseEditable();

    return caseDetailBase.canSubmit();
}

public void createProjId(ProjId _createProjId = createProjId)
{
    if (caseDetailBase.RecId && CaseDetailBase.getInstanceRelationType() == tableStr(CaseDetail))
    {
        caseDetailBase_CaseDetail.ProjId = _createProjId;
        caseDetailBase_CaseDetail.write();

        caseDetailBase_CaseDetail_ds.refresh();
    }
}

public void createRecord(str _formDataSourceName, boolean _append = false)
{
    if (_formDataSourceName == caseDetailBase_ds.name())
    {
        if (element.args() && element.args().record())
        {
            caseDetailForm.parmCallerRecord(element.args().record());
        }

        if (createDependentCase)
        {
            caseDetailForm.parmCaseDetailParent(caseDetailBase);
        }
        else
        {
            caseDetailForm.parmCaseDetailParent(null);
        }

        if (caseDetailForm.create())
        {
            newCaseDetail = caseDetailForm.parmCaseDetail();

            if (newCaseDetail)
            {
                caseDetailBase.data(newCaseDetail);
                caseDetailBase_ds.research();
                caseDetailBase_ds.positionToRecord(newCaseDetail);
                caseDetailBase_ds.rereadReferenceDataSources();
            }
        }
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

public void enableCAPASubCategory()
{
    boolean enableSubCategory = (PIPCAPACategories::find(caseDetailBase.PIPCategory).SubCategoryEntityType == PIPCAPASubCategoryEntityType::UserDefined);

    caseDetailBase_PIPCAPASubCategory.enabled(enableSubCategory);
}

public void init()
{
    CaseDetailBase  caseDetailBaseLocal;
    HRMParameters   hrmParameters;
    boolean         isCallerFmlaListPage;
    boolean         isFMLACase;

    caseDetailForm = new CaseDetailForm();

    if (element.args().parmEnumType() == enumNum(CaseCategoryType) && element.args().parmEnum())
    {
        caseDetailForm.parmCategoryType(element.args().parmEnum());
    }

    if (element.args().dataset() == tableNum(CaseDetailBase))
    {

        // If args().record() is a CaseDetailBase record and caller is null,
        // then need to assign its CaseId to args().parm(). This is used when
        // clicking a work item in Role Center.
        if (element.args().caller() == null)
        {
            if (this.args().record() != null && this.args().record() is CaseDetailBase)
            {
                caseDetailBaseLocal = this.args().record();
            }
            else if (this.args().lookupRecord() != null && this.args().lookupRecord() is CaseDetailBase)
            {
                caseDetailBaseLocal = this.args().lookupRecord();
            }

            if (caseDetailBaseLocal)
            {
                this.args().parm(caseDetailBaseLocal.CaseId);
            }
        }
    }

    super();

    // Check to see if Payroll is enabled for FMLA cases.
    isPayrollEnabled = isConfigurationkeyEnabled(configurationKeyNum(Payroll));

    // setting the more information panel.
    element.showMoreInformation();
    element.updateDesign(InventDimFormDesignUpdate::Init);
    hrmParameters = HRMParameters::find();

    if (hrmParameters.FMLAEligibilityPeriodType == HcmFMLAEligibilityPeriodType::Months)
    {
        Eligibility_LengthOfService.label("@SYP4960027" + ": " + "@SYP4960029");
    }
    else
    {
        Eligibility_LengthOfService.label("@SYP4960027" + ": " + "@SYP4960028");
    }

    // Get the menu item for this form.
    isCallerFmlaListPage = false;
    isFMLACase = false;

    isCallerFmlaListPage = element.args().menuItemName() == menuItemDisplayStr(HcmFMLACaseListPage);

    if (this.args().record() != null && this.args().record() is HcmFMLACaseDetail)
    {
        isFMLACase = true;
    }

    // Hide and show fields in grid related FMLA.
    grid_Description.visible(!isCallerFmlaListPage);
    Grid_Status.visible(!isCallerFmlaListPage);
    QueueName.visible(!isCallerFmlaListPage);
    AssignToQueue.visible(!isCallerFmlaListPage);

    CaseDetail_HcmFMLACaseDetail_LeaveSchedule.visible(isCallerFmlaListPage);
    CaseDetail_HcmFMLACaseDetail_LeaveReason.visible(isCallerFmlaListPage);
    CaseDetail_HcmFMLACaseDetail_LeaveStartDate.visible(isCallerFmlaListPage);
    CaseDetail_HcmFMLACaseDetail_EstimatedLeaveEndDtae.visible(isCallerFmlaListPage);

    // Worker name and case category cannot be changed once a FMLA case is created.
    DirPartyTable_Name.allowEdit(!isFMLACase);
    //CaseCatHierarchyDetail_CaseCategory.allowEdit(!isFMLACase);

    element.showQueueInformation();

    caseDetailForm.formMethodDataSourceInit(caseDetailBase_ds, element.args());
    hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newCustomOptions(true, true, true, false, true, true, true, true, true, false, true);
    hcmWorkerLookupActiveWorkers = HcmWorkerLookup::newActiveWorkers();

}

private void loadEntityNameToEnumMap()
{
    SysDictEnum     dictEnum        = new SysDictEnum(enumNum(CaseEntityType));
    int             i;
    CaseEntityType  entityValue;
    int             maxNumEnums;

    void addToMap(CaseEntityType _value)
    {
        entityNameToEntityTypes.insert(enum2str(_value), _value);
    }

    entityNameToEntityTypes = new Map(Types::String, Types::Enum);
    entityTypesToIgnore     = conNull();

    addToMap(CaseEntityType::None);
    addToMap(CaseEntityType::Customer);
    addToMap(CaseEntityType::Vendor);
    addToMap(CaseEntityType::SalesTable);
    addToMap(CaseEntityType::PurchTable);
    addToMap(CaseEntityType::ProductionOrder);
    addToMap(CaseEntityType::Employee);
    addToMap(CaseEntityType::Item);
    addToMap(CaseEntityType::ServiceOrder);
    addToMap(CaseEntityType::QualityOrder);
    addToMap(CaseEntityType::QuarantineOrder);
    addToMap(CaseEntityType::NonConformance);
    addToMap(CaseEntityType::PurchLine);
    addToMap(CaseEntityType::SalesLine);
    addToMap(CaseEntityType::Case);
    addToMap(CaseEntityType::PIPCAPACase);
    addToMap(CaseEntityType::Product);
    addToMap(CaseEntityType::Route);
    addToMap(CaseEntityType::BOM);
    addToMap(CaseEntityType::ReturnOrder);

    this.onLoadEntitytNameEnumMap(entityNameToEntityTypes);

    Set usedValues = new Set(Types::Enum);
    MapEnumerator entityTypeEnumerator = entityNameToEntityTypes.getEnumerator();
    while (entityTypeEnumerator.moveNext())
    {
        usedValues.add(entityTypeEnumerator.currentValue());
    }

    maxNumEnums = dictEnum.values();
    for (i = 1; i <= maxNumEnums; i++)
    {
        entityValue = dictEnum.index2Value(i);
        if (!usedValues.in(entityValue))
        {
            entityTypesToIgnore += [entityValue];
        }
    }
}

