void setButtonControls(FormDataSource _fds)
{
    boolean enableDetails;

    switch (_fds.name())
    {
        case caseLog_ds.name() :                
            caseLogDetailsButton.enabled(caseLog.RecId != 0 && caseLog.SourceTypeId != "" && caseLogGrid_M_editSourceLinkedId.valueStr() != "");               
            break;

        case caseAssociation_ds.name() :
        case PIPCAPACaseDimAssociationTmp_ds.name():
            enableDetails = (caseAssociation.RecId != 0);
            if (enableDetails && (caseAssociation.EntityType == CaseEntityType::SalesTable || caseAssociation.EntityType == CaseEntityType::PurchTable))
            {
                // crosscompany lookup for SOs & POs are not supported
                enableDetails = (caseAssociation.linkedCompany() == curext());
            }
            associationDetailsButton.enabled(enableDetails);
            break;
    }
}

/// <summary>
///     Enable the Approve and activate change button if case is of type product change,
///     case state is 'In progress' and electronic signature is not enabled.
/// </summary>
void setButtonControlsEngineeringChange()
{
    CaseAssociation     engChgCaseAssociation;
    boolean             isEngChgCase    = (caseDetailBase.caseCategory().CategoryType == CaseCategoryType::EngineeringChange);
    boolean             isRulesEnabled  = EngChgCaseCategoryValidationRules::rulesEnabledForCaseCategory(caseDetailBase.caseCategory().RecId);
    boolean             isBOMAndRouteApprovalEnabled;

    isBOMAndRouteApprovalEnabled = isConfigurationKeyEnabled(configurationkeynum(BOMApprove))
        && isConfigurationKeyEnabled(configurationkeynum(RouteApprove));

    select firstOnly engChgCaseAssociation where engChgCaseAssociation.CaseRecId == caseDetailBase.RecId;

    engChgCaseManageApproval.enabled(isEngChgCase
                                     && isBOMAndRouteApprovalEnabled
                                     && engChgCaseAssociation
                                     && caseDetailBase.Status == CaseStatus::InProcess
                                     && !EngChgCaseBOMRouteApprovalHelper::electronicSignatureUsed());

    engChgCaseCheckCompleteness.enabled(isEngChgCase
                                        && isRulesEnabled
                                        && engChgCaseAssociation
                                        && (caseDetailBase.Status == CaseStatus::Planned
                                        || caseDetailBase.Status == CaseStatus::InProcess));

    EngineeringChangeGroup.visible(isEngChgCase);
}

void setButtonControlsMain()
{
    element.setCaseEditable();
    element.setProcessControls();
    element.setButtonControlsMain_Update();
    element.setButtonControlsMain_SLA();
    element.setSignatureReviewControl();

    sendMail.enabled(caseDetailBase.EmailId != '');

    // Added another check to skip FMLA case type.
    if (CaseDetailBase.getInstanceRelationType() == tableStr(CaseDetail) && caseDetailBase.getInstanceRelationType() != tableStr(HcmFMLACaseDetail))
    {
        // Non-collection
        projectGroup_ProjId.visible(caseDetailBase_CaseDetail.RecId != 0);
        sla_Log.visible(caseDetailBase_CaseDetail.RecId != 0);
        connectToProject.visible(caseDetailBase_CaseDetail.RecId != 0);
        createProject.visible(caseDetailBase_CaseDetail.RecId != 0);
        detailsTabPage.visible(caseDetailBase_CaseDetail.RecId != 0);

        answersButton.enabled(caseDetailBase_CaseDetail.AnswerId != '');
        questionnaireMenuButton.enabled(caseDetailBase.RecId != 0 && caseDetailBase_CaseDetail.QuestionnaireId && caseDetailBase.ContactPersonId);
        followUpGroup_QuestionnaireId.visible(caseDetailBase_CaseDetail.RecId != 0);
    }

    element.setButtonControlsEngineeringChange();
}

void setButtonControlsMain_SLA()
{
    boolean enable = false;
    SMAServiceLevelAgreementLogTable    logTable;

    if (CaseDetailBase.getInstanceRelationType() == tableStr(CaseDetail))
    {
        sla_Log.enabled(caseDetailBase_CaseDetail.ServiceLevelAgreementId != '');
        sla_Start.enabled(caseDetailBase_CaseDetail.ServiceLevelAgreementId != '' && caseDetailBase_CaseDetail.ServiceLevelAgreementStatus != SMALogStatus::Open) ;
        sla_Stop.enabled(caseDetailBase_CaseDetail.ServiceLevelAgreementId != '' && caseDetailBase_CaseDetail.ServiceLevelAgreementStatus != SMALogStatus::Closed);
        sla_Cancel.enabled(caseDetailBase_CaseDetail.ServiceLevelAgreementId != '' && caseDetailBase_CaseDetail.ServiceLevelAgreementStatus != SMALogStatus::Cancel);

        logTable = SMAServiceLevelAgreementLogTable::find(SMAServiceLevelAgreementLogType::Case, caseDetailBase_CaseDetail.RecId);

        if (caseDetailBase_CaseDetail.Status == CaseStatus::Planned && !logTable)
        {
            enable = true;
        }

        caseDetailBase_CaseDetail_ds.object(fieldNum(CaseDetail, ServiceLevelAgreementId)).allowEdit(enable);
    }
}

