/// <summary>
///     Disable all caseUpdateStatus buttons if current case document is participating an active workflow.
///     Otherwise, check current status and disable those buttons is needed.
///     Do the same to case delete button.
/// </summary>
void setButtonControlsMain_Update()
{
    if (caseExistsInWorkflow)
    {
        updateMenuButton.enabled(false);
        CaseDetailBase_DS.allowDelete(false);
    }
    else
    {
        updateMenuButton.enabled(true);
        CaseDetailBase_DS.allowDelete(true);
        caseUpdateStatus_Planned.enabled(caseDetailBase.RecId != 0 && caseDetailBase.Status != CaseStatus::Planned);
        caseUpdateStatus_InProcess.enabled(caseDetailBase.RecId != 0 && caseDetailBase.Status != CaseStatus::InProcess);
        caseUpdateStatus_Closed.enabled(caseDetailBase.RecId != 0 && caseDetailBase.Status == CaseStatus::InProcess);
        caseUpdateStatus_Cancelled.enabled(caseDetailBase.RecId != 0 && (caseDetailBase.Status != CaseStatus::Cancelled && caseDetailBase.Status != CaseStatus::Closed));
    }
}

