/// <summary>
/// Set the AllowEdit and AllowDelete for case document, according to whether
/// the case is participating an active workflow and current user is the owner.
/// </summary>
void setCaseEditable()
{
    boolean isOwner;

    void setDataSource(boolean allowEdit)
    {
        caseDetailBase_ds.allowEdit(allowEdit);
        caseDetailBase_ds.allowDelete(allowEdit);
        caseDetailBase_CaseDetail_ds.allowEdit(allowEdit);
        caseDetailBase_CaseDetail_ds.allowDelete(allowEdit);
    }

    if (caseExistsInWorkflow)
    {
        isOwner = WorkflowWorkItem::isUserTaskOwner(caseDetailBase);
        setDataSource(isOwner);
    }
    else
    {
        setDataSource(true);
    }
}

