void setHoursWorkedFMLA()
{
    boolean isHoursWorkedChecked;

    isHoursWorkedChecked = hoursWorkedPayrollIntegration.value();

    if (!isPayrollEnabled)
    {
        caseDetailBase_HcmFMLACaseDetail_ds.object(fieldNum(HcmFMLACaseDetail, HoursWorked)).allowEdit(true);
    }
    else
    {
        caseDetailBase_HcmFMLACaseDetail_ds.object(fieldNum(HcmFMLACaseDetail, HoursWorked)).allowEdit(!isHoursWorkedChecked);

        if (isHoursWorkedChecked)
        {
            caseDetailBase_HcmFMLACaseDetail_ds.object(fieldNum(HcmFMLACaseDetail, HoursWorked)).setValue(CaseDetailBase_HcmFMLACaseDetail.getHoursWorked());
        }
    }
}

void setProcessControls()
{
    boolean enable              = false;
    boolean enableCAPAProcess   = false;
    boolean enableChangeStage   = false;

    if(caseDetailBase.Process && (caseDetailBase.Status == CaseStatus::InProcess || caseDetailBase.Status == CaseStatus::Planned))
    {
        enable = true;
    }

    if(caseDetailBase.Process 
        && (caseDetailBase.Status == CaseStatus::InProcess || caseDetailBase.Status == CaseStatus::Planned)
        && (!smmProcessInstance || (smmProcessInstance && smmProcessInstance.parmHierarchyTable())))
    {
        enableCAPAProcess = true;
    }

    if(caseDetailBase.Process && caseDetailBase.Status == CaseStatus::InProcess)
    {
        enableChangeStage = true;
    }

    caseProcessButton.enabled(enableCAPAProcess);
    stageButton.enabled(enableChangeStage);

    if (enable)
    {
        smmProcessInstance = smmProcessInstance::construct(caseDetailBase);
    }

    caseDetailBase_ds.object(fieldNum(CaseDetailBase, Process)).allowEdit(caseDetailBase.Status == CaseStatus::Planned || caseDetailBase.Status == CaseStatus::InProcess);
}

public boolean setProcessInstance(HierarchyName _oldProcess, HierarchyName _newProcess)
{
    DialogButton    answer = DialogButton::No;

    if (_oldProcess && _oldProcess !=_newProcess
        && CaseUpdateStatus::caseHasOpenActivities(CaseDetailBase.RecId))
    {
        // Do you want to remove all open activities?
        answer = Box::yesNoCancel("@SYS109839", DialogButton::Cancel);

        if (answer == DialogButton::Cancel)
        {
            return checkFailed("@SYS25904"); //Update has been canceled.
        }
    }

    smmProcessInstance::write(caseDetailBase, _oldProcess, _newProcess, false, answer == DialogButton::Yes, (caseDetailBase.Status == CaseStatus::InProcess));

    return true;
}

