/// <summary>
///     Capture the task when edit the <c>CaseDetailBase</c> record.
/// </summary>
/// <param name="_taskId">
///     The task id.
/// </param>
/// <returns>
///     Returns 1 if succeeded; otherwise 0.
/// </returns>
/// <remarks>
///     The <c>CaseDetailBase</c> record is marked for update and reread when:
///         - Current company is different from the record company
///         - Form is in edit mode
///         - Form is started in grid view mode
/// </remarks>
public int task(int _taskId)
{
    #Task
    int ret;

    ret = super(_taskId);

    switch (_taskId)
    {
        case #TaskEditRecord :
            if (!element.inViewMode()
                && element.args() != null
                && element.args().formViewOption() == FormViewOption::Grid
                && currentDataAreaId != curext())
            {
                appl.setDefaultCompany(currentDataAreaId, false);
                caseDetailBase.selectForUpdate(true);
                caseDetailBase.reread();
            }
            break;
    }
    return ret;
}

/// <summary>
///     Updates the object reference in hcmWorkerLookupForCaseType to point to the correct HcmWorkerLookup for the case type
/// </summary>
private void updateHcmWorkerLookupForCaseType()
{
    if (caseCategoryHierarchyDetail.CategoryType == CaseCategoryType::FMLA)
    {
        hcmWorkerLookupForCaseType = hcmWorkerLookupActiveWorkers;
    }
    else
    {
        hcmWorkerLookupForCaseType = hcmWorkerLookupCurrentCompany;
    }
}

/// <summary>
///     Adds ranges in the data source to obtain all cases that are participating in an active workflow and
///     are assigned to queues to which the current user is a member.
/// </summary>
/// <param name="_query">
///     The query for the form.
/// </param>
private void applyAssignedToMyQueues(Query _query)
{
    QueryBuildDataSource    qbdsCaseWorkItem;
    QueryBuildDataSource    qbdsWorkflowWorkItemQueueAssignee;
    QueryBuildRange         rangeAssignee;
    QueryBuildDataSource        qbds;

    qbds = _query.dataSourceTable(tableNum(CaseDetailBase));

    qbdsCaseWorkItem        = qbds.addDataSource(tableNum(CaseWorkflowWorkItem));
    qbdsCaseWorkItem.addLink(fieldNum(CaseDetailBase, RecId), fieldNum(CaseWorkflowWorkItem, RefRecId));
    qbdsCaseWorkItem.joinMode(JoinMode::ExistsJoin);

    qbdsWorkflowWorkItemQueueAssignee = qbdsCaseWorkItem.addDataSource(tableNum(WorkflowWorkItemQueueAssignee));
    qbdsWorkflowWorkItemQueueAssignee.joinMode(JoinMode::ExistsJoin);
    qbdsWorkflowWorkItemQueueAssignee.addLink(fieldNum(CaseWorkflowWorkItem, Queue),fieldNum(WorkflowWorkItemQueueAssignee, WorkitemQueue));

    // Current user needs to be a member
    rangeAssignee = qbdsWorkflowWorkItemQueueAssignee.addRange(fieldNum(WorkflowWorkItemQueueAssignee, User));
    rangeAssignee.value(queryValue(curUserId()));
    rangeAssignee.status(RangeStatus::Locked);
}

/// <summary>
///         Adds ranges in the data source to obtain all cases that are participating in an active workflow and
/// are assigned to the current user.
/// </summary>
/// <param name="_query">
///     The query for the form.
/// </param>
private void applyAssignedToMe(Query _query)
{
    QueryBuildDataSource    qbdsCaseWorkItem;
    QueryBuildRange         rangeAssignee;
    QueryBuildDataSource        qbds;

    qbds = _query.dataSourceTable(tableNum(CaseDetailBase));

    qbdsCaseWorkItem        = qbds.addDataSource(tableNum(CaseWorkflowWorkItem));
    qbdsCaseWorkItem.addLink(fieldNum(CaseDetailBase, RecId), fieldNum(CaseWorkflowWorkItem, RefRecId));
    qbdsCaseWorkItem.joinMode(JoinMode::InnerJoin);

    // assigned to current user only
    rangeAssignee = qbdsCaseWorkItem.addRange(fieldNum(CaseWorkflowWorkItem, UserId));
    rangeAssignee.value(queryValue(curUserId()));
    rangeAssignee.status(RangeStatus::Locked);
}

