        showQueueFields = true;
    }

    QueueName.visible(showQueueFields);
    AssignToQueue.visible(showQueueFields);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParmVisible;
    InventDimParm   inventDimParmEnabled;
    InventDim       inventDimTmp;
    InventTable     item;

    if (caseAssociation.EntityType == CaseEntityType::Item)
    {
        item = InventTable::findRecId(caseAssociation.RefRecId);
        inventDimParmVisible = item.inventDimParmActiveDimensions();
    }

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :
        case InventDimFormDesignUpdate::LinkActive    :
        if (!inventDimFormSetup)
            inventDimFormSetup  = InventDimCtrl_Frm_ProductGroup::newFromForm(element);
            inventDimFormSetup.parmSkipOnHandLookUp(true);

            //inventDimParmVisible.initProductDimensionsAllGroups();
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmVisible);
            inventDimFormSetup.parmDimParmVisible(inventDimParmVisible);

        case InventDimFormDesignUpdate::Active        :
            inventDimFormSetup.parmDimParmVisible(inventDimParmVisible);
            inventDimFormSetup.formActiveSetup();
            if (pipCAPACaseDimAssociation.InventDimId)
            {
                inventDim_ds.initValue();
                inventDimParmEnabled.initFromInventDim(InventDim::find(pipCAPACaseDimAssociation.InventDimId));
            }
            else
            {
                inventDimTmp.initFromInventTable(item);
                inventDimTmp = InventDim::findDim(inventDimTmp);
                pipCAPACaseDimAssociation.InventDimId = inventDimTmp.inventDimId;
                inventDim_ds.initValue();
                inventDimParmEnabled.initFromInventDim(inventDimTmp);
            }
            InventDimGroupSetup::newItemId(item.ItemId).inventDimParmActive(inventDimParmEnabled);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange   :
            inventDimFormSetup.formActiveSetup();
            if (!pipCAPACaseDimAssociation.InventDimId)
            {
                inventDimTmp.initFromInventTable(item);
                inventDimTmp = InventDim::findDim(inventDimTmp);
                pipCAPACaseDimAssociation.InventDimId = inventDimTmp.inventDimId;
                inventDim_ds.initValue();
                inventDimParmEnabled.initFromInventDim(inventDimTmp);
            }
            InventDimGroupSetup::newItemId(item.ItemId).inventDimParmActive(inventDimParmEnabled);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }
}

