/// <summary>
///     Locks the form if the case is closed/cancelled
/// </summary>
private void setFormEditable()
{
    boolean isCaseClosedOrCancelled = (caseDetailBase.Status == CaseStatus::Closed || caseDetailBase.Status == CaseStatus::Cancelled);

    if (isCaseClosedOrCancelled)
    { 
        this.design().viewEditMode(SysViewEditMode::View);
    }
    else
    {
        this.design().viewEditMode(SysViewEditMode::Edit);
    }

    caseDetailBase_ds.allowEdit(!isCaseClosedOrCancelled);
    caseLog_ds.allowCreate(!isCaseClosedOrCancelled);
    caseAssociation_ds.allowCreate(!isCaseClosedOrCancelled);
    tmpSmmKACaseRelation_ds.allowCreate(!isCaseClosedOrCancelled);
    pipCAPACaseEmailRec_ds.allowCreate(!isCaseClosedOrCancelled);

    caseLog_ds.allowDelete(!isCaseClosedOrCancelled);
    caseAssociation_ds.allowDelete(!isCaseClosedOrCancelled);
    tmpSmmKACaseRelation_ds.allowDelete(!isCaseClosedOrCancelled);
    pipCAPACaseEmailRec_ds.allowDelete(!isCaseClosedOrCancelled);

    btngrpCaseGeneralSetup.enabled(!isCaseClosedOrCancelled);
    btngrpCaseGeneralFollowUp.enabled(!isCaseClosedOrCancelled);
    btngrpCaseGeneralRelatedInfo.enabled(!isCaseClosedOrCancelled);
    CreateAction.enabled(!isCaseClosedOrCancelled);
    CreateAppointment.enabled(!isCaseClosedOrCancelled);
    CreateEvent.enabled(!isCaseClosedOrCancelled);
    CreateTask.enabled(!isCaseClosedOrCancelled);
    cmdbtnCaseLogNew.enabled(!isCaseClosedOrCancelled);

    caseDetailBase_ds.reread();
    caseDetailBase_ds.refresh();        
}

private void emailCapaStatus(PIPCAPACaseEmailRec _emailRec, CaseStatus _newStatus)
{
    PIPCAPAMailer               mailer;

    mailer = PIPCAPAMailer::construct();
    mailer.parmSubject("@PIP:CAPACaseStatusHeader");
    mailer.parmBody(strFmt("@PIP:CAPACaseStatusBody",
        PIPCAPAMailer::buildAXURL(menuItemDisplayStr(PIPCAPACaseDetail), MenuItemType::Display, formDataSourceStr(PIPCAPACaseDetail, CaseDetailBase), fieldStr(CaseDetailBase, CaseId), CaseDetailBase.CaseId),
        CaseDetailBase.CaseId,
        CaseDetailBase.Description,
        CaseDetailBase.Status,
        _newStatus,
        XUserInfo::find(false, curUserId()).name,
        date2StrUsr(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())),
        CaseDetailBase.Priority,
        date2StrUsr(CaseDetailBase.PIPCAPADueDate),
        CaseDetailBase.PIPCategory,
        CaseDetailBase.PIPCAPASubCategory
        ));

    if (_emailRec.DirPartyRecId)
    {
        mailer.addToAddress(_emailRec.emailAddress());
    }

    if (_emailRec.PIPCAPAWorkerGroupRecId)
    {
        PIPCAPAWorkerGroupAssignment groupAssignment;

        while select groupAssignment
            where groupAssignment.Group == _emailRec.PIPCAPAWorkerGroupRecId
        {
            mailer.addToAddress(groupAssignment.worker().email());
        }
    }

    mailer.send();
}

public void init()
{
    super();

    SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(CaseDetailBase, PIPIsCAPA)).value(queryValue(NoYes::Yes));
}

public void executeQuery()
{
    Common common = caseDetailBase.data();
    Args args = element.args();

    if (args)
    {
        CaseId paramCaseId = args.parm();

        if (paramCaseId)
        {
            QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(CaseDetailBase));
            SysQuery::findOrCreateRange(qbds, fieldNum(CaseDetailBase, CaseId)).value(paramCaseId);
            caseDetailBase_ds.positionToRecordByValue(fieldNum(CaseDetailBase, CaseId), paramCaseId);
        }
    }

    if (args)
    {
        if (args.parmEnumType() == enumNum(CaseListFilter))
        {
            if (args.parmEnum() == CaseListFilter::AssignedToMyQueues)
            {
                element.applyAssignedToMyQueues(this.query());
            }
            else  if (args.parmEnum() == CaseListFilter::AssignedToMe)
            {
                element.applyAssignedToMe(this.query());
            }
        }
    }

    if (!linkActiveCalled
        && args
        && args.dataset()
        && CaseAssociation::getTypeForRecord(args.record()) != CaseEntityType::PIPCAPACase)
    {
        associationLink = new smmAssociationLink(this.query());
        associationLink.addRelatedCaseRanges(args.record());
    }

    super();

    if (common)
    {
        CaseDetailBase_ds.positionToRecord(common);
        CaseDetailBase_ds.setCurrent();
    }
}

public void write()
{
    boolean         ret;
    HierarchyName   oldProcess;

    ttsbegin;

    caseDetailForm.parmCaseDetail(caseDetailBase);
    caseDetailForm.parmDirPartyTable(dirPartyTable);
    caseDetailForm.formMethodDataSourceWrite(element, this);

    oldProcess = caseDetailBase.orig().Process;

    super();

    if (oldProcess != caseDetailBase.Process)
    {
        // Case process
        ret = element.setProcessInstance(oldProcess, caseDetailBase.Process);

        if (!ret)
        {
            ttsabort;
            caseDetailBase_ds.research();
            return;
        }
    }

    ttscommit;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = caseDetailForm.formMethodDataSourceValidateWrite(element, this);
    }

    return ret;
}

public void linkActive()
{
    Hierarchy   hierarchy;
    Object      myCaller;
    FormRun     callerForm;

    caseDetailForm.formMethodDataSourceLinkActive(element, this);
    myCaller = element.args().caller();

    if (element.args())
    {
        switch (element.args().dataset())
        {
            case tableNum(CaseCategoryHierarchyDetail) :
                this.query().dataSourceTable(tableNum(CaseDetailBase)).addRange(fieldNum(CaseDetailBase, CategoryRecId)).value(queryValue(element.args().record().RecId));
                break;

            case tableNum(CaseDetailBase) :
            case tableNum(CaseDetail):
                if (myCaller && myCaller is FormRun)
                {
                    callerForm = myCaller;
                    this.query().dataSourceTable(tableNum(CaseDetailBase)).addRange(fieldNum(CaseDetailBase, RecId)).value(queryValue(element.args().record().RecId));
                }
                break;

            case tableNum(Hierarchy) :
                hierarchy = element.args().record();
                this.query().dataSourceTable(tableNum(CaseDetailBase)).addRange(fieldNum(CaseDetailBase, Process)).value(hierarchy.Name);
                break;

            default :
                associationLink = new smmAssociationLink(this.query());
                associationLink.addRelatedCaseRanges(element.args().record());
                break;
        }
    }

    linkActiveCalled = true;
    super();
}

