}

[SysClientCacheDataMethodAttribute(true)]
display Name getQueueName()
{
    CaseWorkflowWorkItem caseWorkflowWorkItem;
    select firstonly Name from caseWorkflowWorkItem where caseWorkflowWorkItem.RefTableId == tablenum(CaseDetailBase)
                                                && caseWorkflowWorkItem.RefRecId == CaseDetailBase.RecId;
    return caseWorkflowWorkItem.Name;
}

[SysClientCacheDataMethodAttribute(true)]
display utcdatetime getAssignedToQueue()
{
    CaseWorkflowWorkItem caseWorkflowWorkItem;
    select firstonly AssignToQueueTime from caseWorkflowWorkItem where caseWorkflowWorkItem.RefTableId == tablenum(CaseDetailBase)
                                                && caseWorkflowWorkItem.RefRecId == CaseDetailBase.RecId;
    return caseWorkflowWorkItem.AssignToQueueTime;
}

