public boolean validate()
{
    smmEncyclopediaItems    encyclopediaTable = smmEncyclopediaItems::find(smmEncyclopediaItems_ItemId.valueStr());
    boolean ret;

    ret = super();

    if (ret && smmKACategoryRelation::existId(encyclopediaTable.ItemId,caseDetailBase.CategoryRecId))
    {
        //This article is already associated to the category.
        ret = checkFailed("@SYS135138");
    }

    if (ret && smmKACaseRelation::existId(encyclopediaTable.ItemId,caseDetailBase.RecId))
    {
        //This article is already associated to the case.
        ret = checkFailed("@SYS135139");
    }

    return ret;
}

