/// <summary>
///     Resolves the custom lookup for the <c>HcmFMLACaseAssociation</c> table records that filters on the worker and date range of the case.
/// </summary>
/// <param name="_formReferenceControl">
///     The control on the form that is used.
/// </param>
/// <returns>
///     A reference record for the purpose.
/// </returns>
public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    HcmEmploymentLeave employmentLeave;

    employmentLeave = super(_formReferenceControl);

    if (!HcmFMLACaseAssociationLookup::validateCaseAssociation(casedetailBase_HcmFMLACaseDetail.Party,
                                                               caseDetailBase_HcmFMLACaseDetail.LeaveStartDate,
                                                               caseDetailBase_HcmFMLACaseDetail.EstimatedLeaveEndDate))
    {
        employmentLeave = null;
    }

    return employmentLeave;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    HcmFMLACaseAssociationLookup caseAssociationLookUp;
    HcmEmploymentLeave           employmentLeave;

    caseAssociationLookUp = new HcmFMLACaseAssociationLookup();

    employmentLeave = caseAssociationLookUp.lookupCaseAssociatedLeave(_formReferenceControl,
                                                                        casedetailBase_HcmFMLACaseDetail.Party,
                                                                        caseDetailBase_HcmFMLACaseDetail.LeaveStartDate,
                                                                        caseDetailBase_HcmFMLACaseDetail.EstimatedLeaveEndDate);

    return employmentLeave;
}

