public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret && pipCAPACaseDimAssociationTmp.EntityType == CaseEntityType::Item && InventDim.inventBatchId && InventDim.inventSerialId)
    {
        if (ret && InventDim.inventBatchId)
        {
            InventDim   validateInventDim;
            InventSum   inventSum;

            select forceplaceholders firstonly RecId from validateInventDim
                    where validateInventDim.InventSerialId  == InventDim.inventSerialId
                        && validateInventDim.inventBatchId == InventDim.inventBatchId
                    join TableId from inventSum
                        index hint DimIdClosedQtyIdx
                        where inventSum.InventDimId     == validateInventDim.InventDimId    &&
                              inventSum.ItemId          == InventTable::findRecId(pipCAPACaseDimAssociationTmp.RefRecId).ItemId;

            if (!validateInventDim.RecId)
            {
                ret = checkFailed(strFmt("@PIP:BatchSerialInvalidCombo", InventDim.InventBatchId, InventDim.InventSerialId));
            }
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (pipCAPACaseDimAssociationTmp.EntityType == CaseEntityType::Item)
    {
        ret = InventSerial::checkExist(InventDim.inventSerialId, InventTable::findRecId(pipCAPACaseDimAssociationTmp.RefRecId).ItemId);
    }
    return ret;
}

