    int ret;

    ret = super();

    element.showMoreInformation();
    element.setCaseAssociationEditable();

    if(PIPCAPACaseDimAssociationTmp.EntityType == CaseEntityType::Item)
    {
        element.updateDesign(InventDimFormDesignUpdate::Active);

        PIPCAPACaseDimAssociation.PdsCWUnitId = PdsCatchWeight::cwUnitId(InventTable::findRecId(CaseAssociation.RefRecId).ItemId);
    }

    return ret;

}

