    element.setButtonControls(this);
    element.showMoreInformation();
}

public boolean validateWrite()
{
    boolean ret;
    PIPCAPACaseDimAssociation caseDimAssociation;
    CaseAssociation validateCaseAssociation;

    ret = super();

    if (ret)
    {
        if (PIPCAPACaseDimAssociationTmp.EntityType == CaseEntityType::Item)
        {
            InventTable localInventTable = InventTable::findRecId(PIPCAPACaseDimAssociationTmp.RefRecId);

            if (!PIPCAPACaseDimAssociationTmp.InventDimId)
            {
                InventItemInventSetup itemInventSetup;

                select itemInventSetup
                    where itemInventSetup.ItemId == localInventTable.ItemId;

                PIPCAPACaseDimAssociationTmp.InventDimId = itemInventSetup.InventDimIdDefault;
            }
        }

        if (PIPCAPACaseDimAssociationTmp.EntityType == CaseEntityType::None || !PIPCAPACaseDimAssociationTmp.RefRecId)
        {
            // Association type and id must be specified.
            ret = checkFailed("@SYS133038");
        }
        else if (PIPCAPACaseDimAssociationTmp.IsPrimary)
        {
            select firstonly validateCaseAssociation
                where validateCaseAssociation.CaseRecId == PIPCAPACaseDimAssociationTmp.CaseRecId
                    && validateCaseAssociation.EntityType != PIPCAPACaseDimAssociationTmp.EntityType
                    && validateCaseAssociation.RefRecId != PIPCAPACaseDimAssociationTmp.RefRecId
                    && validateCaseAssociation.IsPrimary;

            if (validateCaseAssociation)
            {
                // There can only be one primary link.
                ret = checkFailed("@SYS102768");
            }
        }

        select firstonly caseDimAssociation
            where caseDimAssociation.InventDimId == PIPCAPACaseDimAssociationTmp.InventDimId;

        select firstonly validateCaseAssociation
                where validateCaseAssociation.RecId == caseDimAssociation.CaseAssociation
                        && validateCaseAssociation.CaseRecId == PIPCAPACaseDimAssociationTmp.CaseRecId
                        && validateCaseAssociation.RefRecId == PIPCAPACaseDimAssociationTmp.RefRecId
                        && validateCaseAssociation.EntityType == PIPCAPACaseDimAssociationTmp.EntityType;

        if (!PIPCAPACaseDimAssociationTmp.RecId && validateCaseAssociation.RecId)
        {
            // If the case association record already exists.
            ret = checkFailed(strFmt("@SYS4009268",
                            validateCaseAssociation.EntityType,
                            validateCaseAssociation.linkedId(false, ''),
                            validateCaseAssociation.linkedName()));
        }

    }

    return ret;
}

public void delete()
{
    RefRecId caseAssociationRecId = PIPCAPACaseDimAssociationTmp.CaseAssociation;
    RefRecId pipCAPACaseDimAssociationRecId = PIPCAPACaseDimAssociationTmp.PIPCAPACaseDimAssociation;

    super();
    PIPCAPACaseDimAssociationTmp::deleteAssociations(caseAssociationRecId, pipCAPACaseDimAssociationRecId);
}

