public void lookup()
{
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(ContactPerson), this);
    Query                   query = new Query();
    QueryBuildDataSource    qbds;

    sysTableLookup.addLookupfield(fieldNum(ContactPerson, ContactPersonId), true);
    sysTableLookup.addLookupfield(fieldNum(ContactPerson, Party));
    sysTableLookup.addLookupMethod(tableMethodStr(ContactPerson, email));

    qbds = query.addDataSource(tableNum(ContactPerson));
    qbds.addRange(fieldNum(ContactPerson, ContactForParty)).value(queryValue(caseDetailBase.Party));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

