public Common resolveReference()
{
    HcmWorker ret;

    ret = super();

    if (ret != null && !hcmWorkerLookupForCaseType.validateWorker(ret.RecId))
    {
        ret = null;
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (caseDetailBase_HcmFMLACaseDetail.Approved == NoYes::Yes)
    {
        caseDetailBase_HcmFMLACaseDetail.ApprovedBy = curUserId();
    }
    else if (caseDetailBase_HcmFMLACaseDetail.Approved == NoYes::No)
    {
        caseDetailBase_HcmFMLACaseDetail.ApprovedBy = "";
    }

    return ret;
}

