Method.Source:
[Form]
public class PIPInventCAPACaseList extends FormRun implements SysIFilterEventHandler, SysIFilterConsumerForm
{
    boolean enableChangeStage;

}

/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    CaseDetailBase_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

/// <summary>
/// Event handler to react on a filter change.
/// </summary>
public void onFilterChanged()
{
    CaseDetailBase_DS.executeQuery();
}

/// <summary>
///
/// </summary>
public void setButtonControls()
{
    updateMenuButton.enabled(true);

    caseUpdateStatus_Planned.enabled(caseDetailBase.RecId != 0 && caseDetailBase.Status != CaseStatus::Planned);
    caseUpdateStatus_InProcess.enabled(caseDetailBase.RecId != 0 && caseDetailBase.Status != CaseStatus::InProcess);
    caseUpdateStatus_Closed.enabled(caseDetailBase.RecId != 0 && caseDetailBase.Status == CaseStatus::InProcess);
    caseUpdateStatus_Cancelled.enabled(caseDetailBase.RecId != 0 && (caseDetailBase.Status != CaseStatus::Cancelled && caseDetailBase.Status != CaseStatus::Closed));

    if(caseDetailBase.Process && caseDetailBase.Status == CaseStatus::InProcess)
    {
        enableChangeStage = true;
    }
    stageButton.enabled(enableChangeStage);
}

/// <summary>
///
/// </summary>
/// <returns></returns>
public int active()
{
    int ret;
    enableChangeStage = false;

    ret = super();

    element.setButtonControls();

    return ret;
}
