public class PIPDispensing extends FormRun
{
    ProdJournalFormTransBOM journalFormTrans;
    ProdJournalFormTable    journalFormTable;

    PdsCWFormCtrl           cwFormControl;

    boolean                 scalesDeviceManuallySet;

}

public void doRefresh()
{
    ProdJournalBOM           pmfProdJournalBOM;

    pmfProdJournalBOM = prodJournalBOM.data();
    prodJournalBOM_ds.research();
    prodJournalBOM_ds.positionToRecord(pmfProdJournalBOM);
}

/// <summary>
///    Refreshes the data source.
/// </summary>
/// <remarks>
///    When the <c>InventTransPick</c> form is closed, it looks for a <c>doResearch</c> method on the calling form
///    in order to reflect any updates on the caller datasource.
/// </remarks>
public void doResearch()
{
    this.doRefresh();
}

public void init()
{
    PdsCWValidation_PIPDispensing   cwValidator;
    boolean                         dispensingPossibleForAllItems;
    container                       prodIdsEnabledForDispensing;

    ProdJournalTable journal = element.args().record() as ProdJournalTable;
    if (journal)
    {
        [dispensingPossibleForAllItems, prodIdsEnabledForDispensing] = journal.pipDispensingOrders();
        if (!dispensingPossibleForAllItems && prodIdsEnabledForDispensing == conNull())
        {
            throw error("@PIP918");
        }
    }

    journalFormTrans = new PIPDispensingTicketFormTrans(element);
    journalFormTable = new ProdJournalFormTable(element);

    journalFormTrans.parmJournalFormTable(journalFormTable);

    cwFormControl = PdsCWFormCtrl::constructFromForm(element);
    cwValidator = PdsCWValidation_PIPDispensing::construct();
    cwValidator.parmFieldIds(PdsCWValidation::fieldMapFromContainers([[fieldNum(PIPDispensingTicketLine, DispensedQty), fieldNum(PIPDispensingTicketLine, DispensedCWQty), true]]));
    cwFormControl.parmCWValidation(cwValidator);

    super();

    journalFormTrans.parmCtrlCheckJournal(checkJournal);
    journalFormTrans.parmCtrlPostJournal(postJournal);
    journalFormTrans.parmCtrlInfolog(showInfolog);
    journalFormTrans.parmCtrlVoucherTab(voucher);
    journalFormTrans.parmPIPSignatureReviewButton(PIPSIGSignatureReview);

    cwFormControl.parmCWValidation().parmBuffer(PIPDispensingTicketLine);

    journalFormTable.formInitPost();

    this.updateControls();
}

public int task(int _taskId)
{
    int ret;
    #task;

    ret = super(_taskId);

    if(_taskId == #TaskEditRecord || _taskId == #taskDoneEditRecord)
    {
        journalFormTrans.editViewModeChanged();
    }

    return ret;
}

public void onJournalTableChange()
{
    boolean allow = !prodJournalTable.Posted && ProdJournalBOM.RecId != 0;

    PIPDispensingTicketLine_ds.allowCreate(allow);
    PIPDispensingTicketLine_ds.allowDelete(allow);
    PIPDispensingTicketLine_ds.allowEdit(allow);

    this.updateControls();
    this.updateLineControls();
}

private PIPScalesDevice findScalesDevice(PIPScalesDeviceId _deviceId, boolean _forNetwork)
{
    PIPScalesDevice scalesDevice = PIPScalesDevice::find(_deviceId);
    if (!scalesDevice.RecId)
    {
        error(strFmt("@PIP5088", _deviceId));
        return null;
    }
    if (!scalesDevice.isAvailable(_forNetwork))
    {
        return null;
    }

    return scalesDevice;
}

public void setOrCreateWeight(FieldId _qtyFieldId)
{
    container               weights;
    PIPScalesDeviceId       scaleId;
    InventQty               weight;
    UnitOfMeasureSymbol     unitId;
    InventTestInstrument    testInstrument;

    PIPScalesDevice scalesDevice;
    if (ScalesDeviceId.text())
    {
        scalesDevice = this.findScalesDevice(ScalesDeviceId.text(), true);
        if (!scalesDevice)
        {
            return;
        }
        weights = PIPScalesInterface::newDevice(scalesDevice).getFromScales();
    }
    else
    {
        weights = PIPDispensingTicketLine::getWeightDataFromFile();
    }

    if (weights == conNull())
    {
        return;
    }

    [scaleId, weight, unitId] = weights;

    if (!ScalesDeviceId.text())
    {
        scalesDevice = this.findScalesDevice(scaleId, false);
        if (!scalesDevice)
        {
            return;
        }
    }

    if (!unitId)
    {
        unitId = scalesDevice.defaultUnitId();
    }

    // Convert the weighing unit to BOM line unit
    EcoResReleasedProductUnitConverter converter = EcoResReleasedProductUnitConverter::construct();
    weight = converter.convertGivenUnitSymbols(weight, unitId, ProdJournalBOM.BOMUnitId, NoYes::No, ProdJournalBOM.ItemId, ProdJournalBOM.InventDimId);

    // Set the field in the current record
    if (PIPDispensingTicketLine_ds.numberOfRowsLoaded() > 0 && PIPDispensingTicketLine.(_qtyFieldId) == 0)
    {
        PIPDispensingTicketLine.ScalesDeviceId = scalesDevice.DeviceId;
        PIPDispensingTicketLine.(_qtyFieldId)  = weight;
    }
    // Save current line and create a new one with the given field filled
    else
    {
        if (PIPDispensingTicketLine_ds.numberOfRowsLoaded() > 0)
        {
            PIPDispensingTicketLine_ds.write();
        }

        PIPDispensingTicketLine_ds.create();
        PIPDispensingTicketLine.ScalesDeviceId = scalesDevice.DeviceId;
        PIPDispensingTicketLine.(_qtyFieldId)  = weight;
    }
}

public void updateControls()
{
    boolean cwItem = PdsGlobal::pdsIsCWItem(ProdJournalBOM.ItemId);

    DispositionCode.visible(InventoryDimensions_inventBatchId.visible());
    DispositionStatus.visible(InventoryDimensions_inventBatchId.visible());
    PdsResetDispositionStatus.enabled(InventoryDimensions_inventBatchId.visible() && InventDim.InventBatchId != "");

    PIPDispensingTicketLine_ds.object(fieldNum(PIPDispensingTicketLine, DispensedCWQty)).visible(cwItem);

    project1.visible(prodJournalBOM.isProjMethodConsumed());

    if (!scalesDeviceManuallySet)
    {
        ScalesDeviceId.text(ProdParametersDim::find(ProdJournalBOM.InventDimId).PIPScalesDeviceId);
    }
}

public void updateLineControls()
{
    boolean allow = !prodJournalTable.Posted && ProdJournalBOM.RecId != 0;

    NetWeightButton.enabled(allow);
    GrossWeightButton.enabled(allow);
    TareWeightButton.enabled(allow);
    Confirm.enabled(allow);

    PostDispensedButton.enabled(allow && (PIPDispensingTicket.anyIncluded(PIPDispensingTicketLine.RecId) || PIPDispensingTicketLine.IncludeInValidation));
}

public void initDatasourceAllow()
{
    ProdJournalTable_ds.allowCreate(false);
    ProdJournalTable_ds.allowDelete(false);
    ProdJournalTable_ds.allowEdit(false);
}

void init()
{
    super();

    journalFormTable.datasourceInitPost(prodJournalTable);

    Args args = element.args();
    Object caller = args.caller();

    if(caller && caller.name() == formStr(ProdJournalTable))
    {
        QueryBuildDataSource dataSource = ProdJournalTable_DS.query().dataSourceTable(tableNum(ProdJournalTable));
        dataSource.clearDynalinks();
        ProdJournalTable journal = args.record();
        dataSource.addRange(fieldnum(ProdJournalTable, JournalId)).value(queryValue(journal.JournalId));
    }

    QueryBuildRange dispensingJournalRange = this.queryBuildDataSource().addRange(fieldNum(ProdJournalTable, PIPIsDispensingJournal));
    dispensingJournalRange.value(queryValue(NoYes::Yes));
    dispensingJournalRange.status(RangeStatus::Hidden);
}

public boolean leave()
{
    boolean ret = journalFormTable.datasourceLeavePre();

    ret = ret && super();

    return ret;
}

