public boolean validateWrite()
{
    boolean ret;

    PIPDispensingTicketLine.PIPDispensingTicket = PIPDispensingTicket.RecId;

    ret = super();

    ret = ret && cwFormControl.dataSourceValidateWrite();

    return ret;
}

public void modified()
{
    InventMovement::setMinMaxQty(prodBOM);

    InventMovement::bufferSetTransQtyUnit(prodBOM);

    super();

    prodBOM_ds.refresh();
}

void clicked()
{
    if (prodJournalTable.pipPostDispensingTicket())
    {
        info("@PIP919");

        ProdJournalBOM_ds.research(true);
    }
    else
    {
        info("@PIP920");
    }
}

