public edit TransDate filterDate(boolean _set, TransDate _filterDate)
{
    if (_set)
    {
        filterDate = _filterDate;

        bomVersion_ds.executeQuery();
        routeVersion_ds.executeQuery();
    }

    return filterDate;
}

