public void init()
{
    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init           :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_EditDimensions::newFromForm(element);
            }
        // Fall through
        case InventDimFormDesignUpdate::Active         :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(itemId));
            inventDimFormSetup.formSetControls(true);
            break;
        case InventDimFormDesignUpdate::FieldChange    :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(itemId));
            InventDimBOMVersion.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(true);
            break;
        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void executeQuery()
{
    QueryBuildDataSource qbdsInventTable = this.queryBuildDataSource();

    qbdsInventTable.clearRanges();
    qbdsInventTable.addRange(fieldNum(InventTable, ItemId)).value(queryValue(itemId));

    super();
}

public void executeQuery()
{
    BOMVersion              bomVersionTmp;
    QueryBuildDataSource    qbdsBOMVersion = this.queryBuildDataSource();

    qbdsBOMVersion.clearRanges();
    qbdsBOMVersion.addRange(fieldNum(BOMVersion, ItemId)).value(queryValue(itemId));

    if (filterQty)
    {
        select maxOf(FromQty) from bomVersionTmp
            where bomVersionTmp.ItemId      == itemId
                && bomVersionTmp.FromQty    <= filterQty;

        qbdsBOMVersion.addRange(fieldNum(BOMVersion, FromQty)).value(strFmt('((FromQty >= %1) && (FromQty <= %2))', queryValue(bomVersionTmp.FromQty), queryValue(filterQty)));
    }

    if (filterDate)
    {
        qbdsBOMVersion.addRange(fieldNum(BOMVersion, RecId)).value(strFmt('((FromDate <= %1) && ((ToDate >= %1) || (ToDate == 0)))', date2StrXpp(filterDate)));
    }

    super();
}

