public void executeQuery()
{
    RouteVersion            routeVersionTmp;
    QueryBuildDataSource    qbdsRouteVersion = this.queryBuildDataSource();

    qbdsRouteVersion.clearRanges();
    qbdsRouteVersion.addRange(fieldNum(RouteVersion, ItemId)).value(queryValue(itemId));

    if (filterQty)
    {
        select maxOf(FromQty) from routeVersionTmp
            where routeVersionTmp.ItemId      == itemId
                && routeVersionTmp.FromQty    <= filterQty;

        qbdsRouteVersion.addRange(fieldNum(RouteVersion, FromQty)).value(strFmt('((FromQty >= %1) && (FromQty <= %2))', queryValue(routeVersionTmp.FromQty), queryValue(filterQty)));
    }

    if (filterDate)
    {
        qbdsRouteVersion.addRange(fieldNum(RouteVersion, RecId)).value(strFmt('((FromDate <= %1) && ((ToDate >= %1) || (ToDate == 0)))', date2StrXpp(filterDate)));
    }

    super();
}

public void jumpRef()
{
    if (!inventTable)
    {
        return;
    }

    RouteVersion::jumpRefRouteVersionRecId(RouteVersion.RecId, element);
}

public void executeQuery()
{
    QueryBuildDataSource    qbdsAssociation = this.queryBuildDataSource();
    str                     orderTypeRange;

    InventTestItemQualityGroup  itemQualityGroup;
    str itemRelationRange = queryValue(itemId);

    while select itemQualityGroup
        where itemQualityGroup.ItemId == itemId
    {
        itemRelationRange = strFmt('%1, %2', itemRelationRange, itemQualityGroup.QualityGroupId);
    }

    orderTypeRange = strFmt('%1, %2, %3',
                        enum2int(InventTestReferenceType::PmfProdCoBy),
                        enum2int(InventTestReferenceType::Production),
                        enum2int(InventTestReferenceType::RouteOpr));

    qbdsAssociation.clearRanges();
    qbdsAssociation.addRange(fieldNum(InventTestAssociationTable, ItemRelation)).value(itemRelationRange);
    qbdsAssociation.addRange(fieldNum(InventTestAssociationTable, OrderType)).value(orderTypeRange);

    if (filterSite)
    {
        qbdsAssociation.addRange(fieldNum(InventTestAssociationTable, InventSiteId)).value(strFmt('%1, %2', filterSite, SysQuery::valueEmptyString()));
    }

    super();
}

