[Form]
public class PIPInventInstrumentCalibrationDetail extends FormRun implements SysIFilterConsumerForm
{
    PIPInventTestInstrumentCalibration  inventTestInstrumentCalibration;
    QueryBuildRange                     qbrTestInstrumentCalibration;
    QueryBuildRange                     qbrCalibrationProcedure;
    QueryBuildRange                     qbrTestInstrument;
    QueryBuildRange                     qbrTestInstrumentTag;

}

/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    PIPInventTestInstrumentCalibration_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

public void init()
{
    Args    args    = this.args();
    FormRun formRun = args.caller();

    if (args.dataset() == tableNum(PIPInventTestInstrumentCalibration) && args.record())
    {
        inventTestInstrumentCalibration = args.record();
    }

    super();
}

public void updateDesign()
{
    boolean calibrationStarted      = pipInventTestInstrumentCalibration.CalibrationStartedBy && pipInventTestInstrumentCalibration.CalibrationStartedDate;
    boolean calibrationCompleted    = pipInventTestInstrumentCalibration.CalibrationCompletedBy && pipInventTestInstrumentCalibration.CalibrationCompletedBy;
    boolean calibrationApproved     = pipInventTestInstrumentCalibration.CalibrationApprovedBy && pipInventTestInstrumentCalibration.CalibrationApprovedDate;
    boolean calibrationFailed       = (pipInventTestInstrumentCalibration.CalibrationResult == PIPInventTestCalibrationResult::Fail);
    boolean existsReopenHist        = PIPInventTestCalibrationReopenHistory::existByCalibration(pipInventTestInstrumentCalibration.RecId);

    StartCalibration.enabled(!calibrationStarted);
    CompleteCalibration.enabled(!calibrationCompleted);
    ApproveCalibration.enabled(!calibrationApproved && !calibrationFailed);
    ReopenCalibration.enabled(calibrationCompleted);
    CalibrationTools.enabled(!calibrationCompleted);

    NewGroup.enabled(pipInventTestInstrumentCalibration.RecId);
    CalibrationFunctionsGroup.enabled(pipInventTestInstrumentCalibration.RecId);        
    PrintGroup.enabled(pipInventTestInstrumentCalibration.RecId && calibrationCompleted);
    ReopenHistory.enabled(calibrationStarted && existsReopenHist);

    pipInventTestInstrumentCalibration_ds.object(fieldNum(PIPInventTestInstrumentCalibration, InventTestInstrument)).allowEdit(!calibrationStarted);
    pipInventTestInstrumentCalibration_ds.object(fieldNum(PIPInventTestInstrumentCalibration, TestInstrumentTagNumber)).allowEdit(!calibrationStarted);
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign();

    pipInventTestInstrumentCalibration_ds.allowEdit(!pipInventTestInstrumentCalibration.CalibrationApprovedDate);

    return ret;
}
