    QueryBuildRange             qbrCalibrationgroup;
    QueryBuildRange             qbrCalibrationProcedure;

    boolean                     overrideUsageData;
    boolean                     calibrationMethodNotRequired;
    boolean                     calibrationRequired;

}

/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    PIPInventTestInstrumentTag_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

public void createNewCalibrationRecord()
{
    #Task
    Args            args = new Args();
    FormRun         formRun;

    if (pipInventTestInstrumentTag.UsageStatus == PIPTestInstrumentUsageStatus::OutOfService
        && Box::okCancel("@PIP768", DialogButton::Cancel) == DialogButton::Cancel)
    {
        return;
    }

    args.name(formStr(PIPInventInstrumentCalibrationDetail));
    args.record(pipInventTestInstrumentTag);
    args.caller(this);

    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.task(#taskNew);
    formRun.wait();
    if (formRun.closedOk())
    {
        pipInventTestInstrumentTag_ds.reread();
        pipInventTestInstrumentTag_ds.research();
        pipInventTestInstrumentTag_ds.refresh();
    }
}

public void init()
{
    Args args = this.args();
    if (args.dataset() == tableNum(PIPInventTestInstrumentTag) && args.record())
    {
        testInstrumentTag = args.record();
    }

    super();

    this.setCalibrationGroupDetails();
}

public void setButtonControls()
{
    boolean isCalibrationRequired           = pipInventTestInstrumentTag.CalibrationRequired;
    boolean existsClosedCalibrationRecord   = PIPInventTestInstrumentCalibration::existClosedCalibrationRecord(pipInventTestInstrumentTag.TestInstrumentId, pipInventTestInstrumentTag.TagNumber);

    PIPInventInstrumentCalibrationDetailMain.enabled(isCalibrationRequired);
    OutOfService.enabled(pipInventTestInstrumentTag.UsageStatus != PIPTestInstrumentUsageStatus::OutOfService);
    Available.enabled(pipInventTestInstrumentTag.UsageStatus != PIPTestInstrumentUsageStatus::Available);

    FunctionActionPaneTab.enabled(isCalibrationRequired);

    FunctionActionPaneTab.enabled(isCalibrationRequired);
    InquiryActionPaneTab.enabled(isCalibrationRequired);
    PrintActionPaneTab.enabled(isCalibrationRequired);
    PIPInstrumentCalibrationLabel.enabled(existsClosedCalibrationRecord);
    PIPInstrumentCalibrationCertificate.enabled(isCalibrationRequired && pipInventTestInstrumentTag.CalibrationCompletedDate);
}

public void setCalibrationGroupDetails()
{
    PIPCalibrationMethod calibrationMethod  = PIPInventCalibrationGroup::find(pipInventTestInstrumentTag.CalibrationGroup).CalibrationMethod;
    boolean isCalibrationMethodPeriodic     = (calibrationMethod == PIPCalibrationMethod::Periodic);
    boolean isCalibrationMethodUsage        = (calibrationMethod == PIPCalibrationMethod::Usage);
    boolean isCalibrationMethodManual       = (calibrationMethod == PIPCalibrationMethod::Manual);

    if (!pipInventTestInstrumentTag.CalibrationGroup)
    {
        GroupCalibrationGroupDetail.visible(false);
        return;
    }

    GroupCalibrationGroupDetail.visible(true);

    pipInventCalibrationGroup_ds.object(fieldNum(PIPInventCalibrationGroup, CalibrationPeriod)).visible(isCalibrationMethodPeriodic && !isCalibrationMethodManual);
    pipInventCalibrationGroup_ds.object(fieldNum(PIPInventCalibrationGroup, PeriodFrequency)).visible(isCalibrationMethodPeriodic && !isCalibrationMethodManual);

    pipInventCalibrationGroup_ds.object(fieldNum(PIPInventCalibrationGroup, MaxUSage)).visible(isCalibrationMethodUsage && !isCalibrationMethodManual);
    pipInventCalibrationGroup_ds.object(fieldNum(PIPInventCalibrationGroup, MaxUsageLifetime)).visible(isCalibrationMethodUsage && !isCalibrationMethodManual);
    pipInventCalibrationGroup_ds.object(fieldNum(PIPInventCalibrationGroup, UsageCountTrigger)).visible(isCalibrationMethodUsage && !isCalibrationMethodManual);
    pipInventCalibrationGroup_ds.object(fieldNum(PIPInventCalibrationGroup, UsageIncrement)).visible(isCalibrationMethodUsage && !isCalibrationMethodManual);
    pipInventCalibrationGroup_ds.object(fieldNum(PIPInventCalibrationGroup, UsageUpdateMethod)).visible(isCalibrationMethodUsage && !isCalibrationMethodManual);
}

public void setFieldControls()
{
    calibrationMethodNotRequired    = (pipInventTestInstrumentTag.CalibrationMethod == PIPCalibrationMethod::None
                                        || pipInventTestInstrumentTag.CalibrationMethod == PIPCalibrationMethod::Manual);

    calibrationRequired             = pipInventTestInstrumentTag.CalibrationRequired;

    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationNextDate)).allowEdit(calibrationMethodNotRequired && calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, UsageCountSinceLastCalibration)).allowEdit(overrideUsageData && calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, LifetimeUsageCount)).allowEdit(overrideUsageData && calibrationRequired);

    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationStartDate)).allowEdit(calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationStartedDate)).allowEdit(calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationStartedDate)).allowEdit(calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationCompletedDate)).allowEdit(calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationApprovedDate)).allowEdit(calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationCompletionAssignee)).allowEdit(calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationProcedure)).allowEdit(calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationApprovalAssignee)).allowEdit(calibrationRequired);
    pipInventTestInstrumentTag_ds.object(fieldNum(PIPInventTestInstrumentTag, CalibrationGroup)).allowEdit(calibrationRequired);

    overrideUsageData = false;
}

public void updateUsageStatus(PIPTestInstrumentUsageStatus _usageStatus)
{
    pipInventTestInstrumentTag.updateUsageStatus(_usageStatus);

    pipInventTestInstrumentTag_ds.research(true);
}

public void initValue()
{
    super();

    if (pipInventTestInstrumentTag.TestInstrumentId && !pipInventTestInstrumentTag.WMSInputAreaId)
    {
        pipInventTestInstrumentTag.WMSInputAreaId = pipInventTestInstrumentTag.testInstrumentType().WMSInputAreaId;
    }
}

public int active()
{
    int ret;

    ret = super();

    element.setFieldControls();
    element.setButtonControls();
    element.setCalibrationGroupDetails();

    return ret;
}

public void executeQuery()
{
    if (testInstrumentTag)
    {
        this.queryBuildDataSource().addRange(fieldNum(PIPInventTestInstrumentTag, RecId)).value(queryValue(testInstrumentTag.RecId));
    }

    super();
}

