public void lookup()
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange1;
    QueryBuildRange         queryBuildRange2;

    date                    batchRecordDateFilter = BatchRecordDate.dateValue();


    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(ProdTable), this);        

    sysTableLookup.addLookupField(fieldNum(ProdTable, ProdId));
    sysTableLookup.addLookupfield(fieldNum(ProdTable, ItemId));        

    queryBuildDataSource = query.addDataSource(tableNum(ProdTable));

    queryBuildDataSource.addSortField(fieldNum(ProdTable, ProdId));



    if (batchRecordDateFilter)
    {
        queryBuildRange1 = queryBuildDataSource.addRange(fieldNum(ProdTable, CreatedDateTime));
        queryBuildRange1.value(SysQuery::value(batchRecordDateFilter));
    }

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    ProdTable   prod;
    boolean     ret;

    ret = super();

    element.parmProdId(this.valueStr());

    prod = ProdTable::find(prodId);
    GGIBatchRecordReport.enabled(prod.ProdStatus == ProdStatus::Released || prod.ProdStatus == ProdStatus::ReportedFinished || prod.ProdStatus == ProdStatus::StartedUp);
    element.refreshDS();
    return ret;
}
