/// <summary>
/// Updates tank gauge reading on the DBR.
/// </summary>
public void updateTankGaugeReadings()
{
    GGIDumpAndBottlingRecord currentDBR;

    DialogButton dialogButton;

    dialogButton = Box::yesNoCancel("@GGI:DialogUpdateTankGaugingReadings", DialogButton::Yes, "@GGI:AdjustQuantity");

    if (dialogButton != DialogButton::Cancel)
    {
        if (dialogButton == DialogButton::Yes)
        {
            ttsbegin;
            if (ggiDumpAndBottlingRecord.Status == GGIDumpAndBottlingRecordStatus::Open)
            {
                ggiDumpAndBottlingRecord.updateGaugeReadings();

                GGIDumpAndBottlingRecord_ds.refresh();
                GGIDumpAndBottlingRecord_ds.reread();
                GGIDumpAndBottlingRecord_ds.research(true);
            }
            else
            {
                currentDBR = GGIDumpAndBottlingRecord::findCurrentForSiteLocation(ggiDumpAndBottlingRecord.InventSiteId, ggiDumpAndBottlingRecord.WMSLocationId);

                if (currentDBR)
                {
                    currentDBR.updateGaugeReadings();
                }
            }
            ttscommit;
        }
    }             
}

/// <summary>
/// Updates the design of the form.
/// </summary>
private void updateDesign()
{
    GGIDumpAndBottlingRecordClose.enabled(GGIDumpAndBottlingRecord.Status == GGIDumpAndBottlingRecordStatus::Open); 
    GGIDumpAndBottlingRecord_ds.allowEdit(GGIDumpAndBottlingRecord.Status == GGIDumpAndBottlingRecordStatus::Open);
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign();

    return ret;
}
