/// <summary>
/// Updates staging records with line number and line creation sequence number.
/// </summary>
/// <param name = "_dmfDefinitionGroupExecution">The definition group for which the line numbers should be updated.</param>
internal static void updateLineNumbers(DMFDefinitionGroupExecution _dmfDefinitionGroupExecution)
{
    GGIReturnOrderLineStaging salesOrderLineV2Staging;
    boolean updateSourceXMLToEntity;

    ttsbegin;

    while select SalesOrderNumber from salesOrderLineV2Staging
        group by SalesOrderNumber
        where salesOrderLineV2Staging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
            && salesOrderLineV2Staging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
    {
        LineNum lineNum = SalesLine::lastLineNum(salesOrderLineV2Staging.SalesOrderNumber) + 1;
        SalesLineCreationSequenceNumber lineCreationSequenceNumber = SalesLine::lastLineCreationSequenceNumber(salesOrderLineV2Staging.SalesOrderNumber) + 1;
        GGIReturnOrderLineStaging updateStaging;
        SalesLine salesLine;

        while select forupdate updateStaging
            where updateStaging.DefinitionGroup == _dmfDefinitionGroupExecution.DefinitionGroup
                && updateStaging.ExecutionId == _dmfDefinitionGroupExecution.ExecutionId
                && updateStaging.SalesOrderNumber == salesOrderLineV2Staging.SalesOrderNumber
            notexists join salesLine
                where salesLine.InventTransId == updateStaging.InventoryLotId
        {
            updateStaging.LineNumber = lineNum;
            updateStaging.LineCreationSequenceNumber = lineCreationSequenceNumber;
            updateStaging.update();

            lineNum ++;
            lineCreationSequenceNumber ++;
            updateSourceXMLToEntity = true;
        }
    }

    ttscommit;

    if (updateSourceXMLToEntity)
    {
        if (GGIReturnOrderLineStaging::createMapping(_dmfDefinitionGroupExecution, fieldStr(GGIReturnOrderLineStaging, LineNumber))
            | GGIReturnOrderLineStaging::createMapping(_dmfDefinitionGroupExecution, fieldStr(GGIReturnOrderLineStaging, LineCreationSequenceNumber)))
        {
            warning(strFmt("@SCM:EntityRemappingWarning", _dmfDefinitionGroupExecution.Entity));
        }
    }
}

/// <summary>
/// Creates the entity field mappings from source to staging area.
/// </summary>
/// <param name = "_dmfDefinitionGroupExecution">The definition group for which the mappings should be created.</param>
/// <param name = "_entityFieldName">The name of the entity field for which the mappings should be created.</param>
/// <returns>true if the mapping is created; otherwise, false.</returns>
private static boolean createMapping(DMFDefinitionGroupExecution _dmfDefinitionGroupExecution, FieldName _entityFieldName)
{
    #OCCRetryCount

    if (!GGIReturnOrderLineStaging::getMappedField(_dmfDefinitionGroupExecution, _entityFieldName).RecId)
    {
        try
        {
            DMFEntity   entity = DMFEntity::find(_dmfDefinitionGroupExecution.Entity);
            TableId     entityTableId = tablename2id(entity.EntityTable);
            DictField   dictField = new DictField(entityTableId, fieldName2id(entityTableId, _entityFieldName));

            ttsbegin;
            DMFSourceXMLToEntityMap sourceXMLToEntityMap;

            sourceXMLToEntityMap.DefinitionGroup = _dmfDefinitionGroupExecution.DefinitionGroup;
            sourceXMLToEntityMap.Entity = _dmfDefinitionGroupExecution.Entity;
            sourceXMLToEntityMap.EntityField = _entityFieldName;
            sourceXMLToEntityMap.EntityFieldSQL = dictField.name(DbBackend::Sql);
            sourceXMLToEntityMap.XMLField = _entityFieldName;
            sourceXMLToEntityMap.FieldType = enum2str(dictField.baseType());
            sourceXMLToEntityMap.FieldSize = dictField.stringLen();
            sourceXMLToEntityMap.insert(false);
            ttscommit;
        }
        catch (Exception::DuplicateKeyException)
        {
            if (xSession::currentRetryCount() >= #RetryNum)
            {
                throw Exception::DuplicateKeyExceptionNotRecovered;
            }
            else
            {
                retry;
            }
        }

        return true;
    }

    return false;
}

private static DMFSourceXMLToEntityMap getMappedField(DMFDefinitionGroupExecution _dmfDefinitionGroupExecution,FieldName _entityFieldName)
{
    DMFSourceXMLToEntityMap sourceXMLToEntityMap;

    if (_dmfDefinitionGroupExecution.DefinitionGroup
        && _dmfDefinitionGroupExecution.Entity
        && _entityFieldName)
    {
        select firstonly sourceXMLToEntityMap
            where sourceXMLToEntityMap.DefinitionGroup  == _dmfDefinitionGroupExecution.DefinitionGroup
               && sourceXMLToEntityMap.Entity           == _dmfDefinitionGroupExecution.Entity
               && sourceXMLToEntityMap.EntityField      == _entityFieldName;
    }

    return sourceXMLToEntityMap;
}
