            //SKS_FDD_10590 -->
            mapfieldValue = this.validateFieldsInMappingTables(_sourceContext, _ledgerJournalTrans, _sks_EFT_BankFormatValidation, _enableLog);
            if(hasMappingData)// SKS_Bug_11831
