protected container getNotesMetaData(str _formatID)
{
    container   notesList;

    //Metadata column names of data elements
    //FormatId,PaymSpec,Note

    switch (_formatID)
    {
        case ('CA-BMO-XML'):
            notesList = this.getNotesMetaData_CA_BMO_XML();
            break;
        case ('CA-BMO-XML-US'):
            notesList = this.getNotesMetaData_CA_BMO_XML_US();
            break;
    }
    return notesList;
}

