/// <summary>
/// Updating the bank code lookpups from the W0X fields.
/// </summary>
protected void updateBankCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {

        case ('CA-CIBC-XML'):
            if(gSKS_EFT_Bank.W01)
            {
                this.updateBankFields('ChannelProfileID', gSKS_EFT_Bank.W01);
            }
            if(gSKS_EFT_Bank.W02)
            {
                this.updateBankFields('OriginID', gSKS_EFT_Bank.W02);
            }
            break;
    }
}

/// <summary>
/// Updating vendor code lookpus from BE0X and Legacy code loookups
/// </summary>
protected void updateVendorCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case ('CA-CIBC-XML'):
            if(gSKS_EFT_VendorPaym.WChargesIndic)
            {
                this.updateVendorFields('ChrgBr', gSKS_EFT_VendorPaym.WChargesIndic, true);
            }
            break;
    }
}
