/// <summary>
/// Updating the bank code lookpups from the W0X fields.
/// </summary>
protected void updateBankCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case ('CA-SCOTIA-XML'):
            if(gSKS_EFT_Bank.W01)
            {
                this.updateBankFields('InitiatingprtyId', gSKS_EFT_Bank.W01);
            }
            if(gSKS_EFT_Bank.W02)
            {
                this.updateBankFields('TestFileIndic', gSKS_EFT_Bank.W02, true);
            }
            break;
    }
}

/// <summary>
/// Updating vendor code lookpus from BE0X and Legacy code loookups
/// </summary>
protected void updateVendorCodelookupValues()
{
    switch(gSKS_EFT_FileFormat.FormatId)
    {
        case 'CA-SCOTIA-XML':
            if(gSKS_EFT_VendorPaym.WBeneMailHandling)
            {
                this.updateVendorFields('RmtLctnMtd', gSKS_EFT_VendorPaym.WBeneMailHandling, true);
            }
            break;
    }
}
