        //UseWithCustomers,UseWithVendors,EDI,UseWithBanktoBank,FundTransferType,UseWithCustomerReimbursement,InvoiceDetail,RemitAvailability,RemitType,APNameType,APAddressType
        //DDNameType,DDAddressType,CRNameType,CRAddressType

        switch (_formatID)
        {
            case ('EU-BNP-XML-AR'):
                paymSpecList = this.getPaymSpecsMetaData_EU_BNP_XML_AR();
                break;

            case ('EU-BNP-XML-ES-AR'):
                paymSpecList = this.getPaymSpecsMetaData_EU_BNP_XML_ES_AR();
                break;

            case ('EU-BNP-XML-AT-AR'):
                paymSpecList = this.getPaymSpecsMetaData_EU_BNP_XML_AT_AR();
                break;

            case ('EU-BNP-XML-NL-AR'):
