protected container getFileFormatFieldsMetaData(str _formatID)
{
    container   paymSpecFieldsList;

    switch (_formatID)
    {
        case ('EU-BNP-XML-AR'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_EU_BNP_XML_AR();
            break;

        case ('EU-BNP-XML-ES-AR'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_EU_BNP_XML_ES_AR();
            break;

        case ('EU-BNP-XML-NL-AR'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_EU_BNP_XML_NL_AR();
            break;
    }

    return paymSpecFieldsList;
}

