protected container getFileFormatFieldsMetaData(str _formatID)
{
    container   paymSpecFieldsList;

    switch (_formatID)
    {
        case ('EU-SEB-SEPA-CREDIT'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_EU_SEB_SEPA_CREDIT();
            break;
    }

    return paymSpecFieldsList;
}

private container getFileFormatFieldsMetaData_EU_SEB_SEPA_CREDIT()
