    paymSpecFieldsList = ConIns(paymSpecFieldsList,conLen(paymSpecFieldsList)+1,['EU-SEB-SEPA-CREDIT','EU-SEB-SEPA-CREDIT','DebtorId','Debtor id',1,'DebtorId','',1,'','XXXXXXXXXXXXXX','GrpHdr > InitgPty > Id > Orgid > Othr > Id']);

