    codeLookupValuesList = ConIns(codeLookupValuesList,conLen(codeLookupValuesList)+1,['JP-SMBC-XML','Btchbkg','N','N']);

    return codeLookupValuesList;
}

protected container getFileFormatFieldsMetaData(str _formatID)
{
    container   paymSpecFieldsList;

    switch (_formatID)
    {
        case ('JP-SMBC-XML'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_JP_SMBC_XML();
            break;
        case ('JP-SMBC-XML-US'):
            paymSpecFieldsList = this.getFileFormatFieldsMetaData_JP_SMBC_XML_US();
            break;
    }

    return paymSpecFieldsList;
}

