    paymSpecFieldsList = ConIns(paymSpecFieldsList,conLen(paymSpecFieldsList)+1,['JP-SMBC-XML','JP-SMBC-XML','DbtrAgtBrnchId','Debtor Branch Id',1,'DbtrAgtBrnchId','',0,'','XXX','DbtrAgt > BrnchId > Id']);

